/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    if ( sServiceName == "rsm2060-service:searchTms" ) { // TMS 값 검색

		if(dsTms.GetRowCount()==0)
		{		
			dsTms.SetColumn(0,"TMS","");
			dsTms.SetColumn(0,"TMS_DESC","없음");
			cboTms.Index=0;
		}
		else
		{
			cboTms.Value = dsTms.GetColumn(0,"MAX_TMS");		
		}
		fcSearch();
	}

	if ( sServiceName == "rsm2060-service:searchBranchAmt" ) 
	{
		if (dsBranchAmt.rowcount()>18) grdBrncCompareSales.Height = 441;
		else                           grdBrncCompareSales.Height = 441 - grdBrncCompareSales.RowHeight;


		dsBranchAmt.row = -1;
		dsSaleGreenDiv.row = -1;
		dsDayOfWeekAmt.row = -1;
		fcTotalYearTmp();// 레포트용 데이타셋 생성
	}


	grdBrncCompareSales.SetFocus();

}


function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch ( obj.ID ) {

		case "cboStndYear" : // 기준년도 변경
			fcSearchTms();
			break;

		case "cboTms" :		// 회차 변경
			fcSearch();
			break;

	}
}



