function fcInit(obj) {
	dsWeather.ClearData();
	dsSales.ClearData();
	dsSpecDesc.clearData();
}



/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchInfo(obj) {
	var sServiceName = "rsm2080-service:searchRaceInfo";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsRaceInfo=dsRaceInfo ";     

	fcd_AddParam("RACE_DAY", calRaceDay.Value);    
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 체크 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    //조회조건 체크및 trim처리
	fcInit();

	if(dsRaceInfo.rowcount == 0) { // 경주일이 있을 경우만 검색
		fnc_message("경주정보가 존재하지 않습니다.");
		return false;
	}

	return true;
}



/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm2080-service:searchSales";  // Grid 조회
	var sInDataSet   = "";
	var sOutDataSet  = "dsWeather=dsWeather ";     
		sOutDataSet += "dsSales=dsSales ";     
		sOutDataSet += "dsEntPrsnNum=dsEntPrsnNum ";
		sOutDataSet += "dsSpecDesc=dsSpecDesc ";   	
		sOutDataSet += "dsMaxSalesRaceNo=dsMaxSalesRaceNo ";   	
		sOutDataSet += "dsRefund=dsRefund ";   	

	if (calRaceDay.Value == today()) {
		sServiceName = "rsm2080-service:searchSalesToday";
	}

	fcd_AddParam("RACE_DAY",   		calRaceDay.Value);        
	fcd_AddParam("MEET_CD",    		rdoMeetCd.Value);        
	fcd_AddParam("STND_YEAR",  		dsRaceInfo.GetColumn(0, "STND_YEAR"));        
	fcd_AddParam("TMS",        		dsRaceInfo.GetColumn(0, "TMS"));        
	fcd_AddParam("DAY_ORD",    		dsRaceInfo.GetColumn(0, "DAY_ORD"));      
	fcd_AddParam("PREV_STND_YEAR",  dsRaceInfo.GetColumn(0, "PREV_STND_YEAR"));      
	fcd_AddParam("PREV_TMS",    	dsRaceInfo.GetColumn(0, "PREV_TMS"));      

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	//변경 카운트는 상위 폼에서 체크 
	if ( !fnc_DatasetChangeCheck(dsWeather) && !fnc_DatasetChangeCheck(dsSpecDesc)) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

	if ( !fcSaveCheck() ) return;

	var sServiceName = "rsm2080-service:saveSpecDesc";
	var sInDataSet   = "dsWeather=dsWeather:u dsSpecDesc=dsSpecDesc:u";
	var sOutDataSet  = "dsWeather=dsWeather ";     
		sOutDataSet += "dsSales=dsSales ";     
		sOutDataSet += "dsEntPrsnNum=dsEntPrsnNum ";
		sOutDataSet += "dsSpecDesc=dsSpecDesc ";   	
		sOutDataSet += "dsMaxSalesRaceNo=dsMaxSalesRaceNo ";   	

	fcd_AddParam("RACE_DAY",   		calRaceDay.Value);        
	fcd_AddParam("MEET_CD",    		rdoMeetCd.Value);        
	fcd_AddParam("STND_YEAR",  		dsRaceInfo.GetColumn(0, "STND_YEAR"));        
	fcd_AddParam("TMS",        		dsRaceInfo.GetColumn(0, "TMS"));        
	fcd_AddParam("DAY_ORD",    		dsRaceInfo.GetColumn(0, "DAY_ORD"));      
	fcd_AddParam("PREV_STND_YEAR",  dsRaceInfo.GetColumn(0, "PREV_STND_YEAR"));      
	fcd_AddParam("PREV_TMS",    	dsRaceInfo.GetColumn(0, "PREV_TMS"));      

	//상황일지 내용 저장 
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);	 

}


