/*-------------------------------------------------------------------------------------------------+
>>  인쇄
+-------------------------------------------------------------------------------------------------*/
function fcPrint(){
	//데이터셋을 초기화 한다.
	ds_reportParam.DeleteAll();
	var nRnum;

	// 필드 값을 셋팅한다.
	var paramField = [
							"RACE_DAY"						// 기준년도 
						,	"MEET_CD"							// 회차 
						,	"STND_YEAR"							// 회차 
						,	"TMS"							// 회차 
						,	"DAY_ORD"							// 회차 
						,	"PREV_STND_YEAR"							// 회차 
						,	"PREV_TMS"							// 회차 
						,   "RACE_YOIL"

					 ];

	// 값을 셋팅한다.
	var paramValue = [
							calRaceDay.Value
						,	rdoMeetCd.Value
						,	dsRaceInfo.GetColumn(0, "STND_YEAR")
						,	dsRaceInfo.GetColumn(0, "TMS")
						,	dsRaceInfo.GetColumn(0, "DAY_ORD")
						,	dsRaceInfo.GetColumn(0, "PREV_STND_YEAR")
						,	dsRaceInfo.GetColumn(0, "PREV_TMS")
						,   dsRaceInfo.GetColumn(0, "RACE_YOIL")
					 ];


	var rowCnt = paramField.length;	
	for(var row = 0 ; row < rowCnt; row++)
	{
		nRnum = ds_reportParam.AppendRow();
		ds_reportParam.SetColumn(nRnum, "PARAM_NM", paramField[row]);
		ds_reportParam.SetColumn(nRnum, "PARAM_VAL",paramValue[row]);
	}

	/* 레포트 배율 추가 - 시작 */
	iRow = ds_reportParam.AddRow();

	// Default Scale 파라미터명 추가(고정)
	ds_reportParam.SetColumn(iRow, "PARAM_NM", "DEFAULT_SCALE");
	ds_reportParam.SetColumn(iRow, "PARAM_VAL", "1");

	//레포트 트랙잭션을 태운다.
	var report_file = "";
	if (dsRaceInfo.GetColumn(0, "DAY_ORD") == "1") report_file = "rsm2080";
	else 									   report_file = "rsm2080_2";
	//Trace(report_file);
	fnc_ReportPreView(report_file, ds_reportParam.ID, "Y");	
}

