/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;
    if (sServiceName = "rsm2080-service:searchRaceInfo")
    {  
    	rdoMeetCd.Value = dsRaceInfo.GetColumn(0,"MEET_CD");	
		stRaceInfo.Text = dsRaceInfo.GetColumn(0,"RACE_INFO");	
		if (dsRaceInfo.GetColumn(0, "DAY_ORD")=="1") specBtnRow.Visible = true;
		else										 specBtnRow.Visible = false;
		grdSales.SetCellProp("head", 1, "text", "전주대비("+dsRaceInfo.GetColumn(0, "STND_YEAR")+"년)");
		grdSales.SetCellProp("head", 2, "text", "전년대비("+dsRaceInfo.GetColumn(0, "PREV_YEAR")+"년)");
		fcSearch();
    }
    else if (sServiceName == "rsm2080-service:searchSales" ||
             sServiceName == "rsm2080-service:searchSalesToday") {			
    	if (dsMaxSalesRaceNo.rowcount() > 0) {
			var maxAmt = dsMaxSalesRaceNo.GetColumn(0, "DIV_TOTAL");
			var parkingCnt = dsSpecDesc.getColumn(0, "PARKING_CNT");
			stMaxSalesInfo.text = "※ 최대 매출액 : 금"+
								fnc_putComma(maxAmt)+"원("+
								dsMaxSalesRaceNo.GetColumn(0, "RACE_NO")+"경주)";
		} else {
			stMaxSalesInfo.text = "";
		}
		if (dsRefund.rowcount() > 0) {
			grdRefund.Visible = true;
			grdSales.Width = 634;
		} else {
			grdRefund.Visible = false;
			grdSales.Width = 784;
		}
	}	
}


