/*-------------------------------------------------------------------------------------------------+
>>  Á¶È¸ ¹öÆ° Å¬¸¯ ãÁ Ã³¸®
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm2110-service:searchDivGoalSalesRslt";  // Grid Á¶È¸
	var sInDataSet   = "";
	var sOutDataSet  = "dsDivGoalSalesRaw=dsDivGoalSalesRaw ";     
        sOutDataSet  += "dsTmsProcessRatio=dsTmsProcessRatio "; 

	fcd_AddParam("DAY_START", calDayStart.Value);    
	fcd_AddParam("DAY_END", calDayEnd.Value);    
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

