			dsDivGoalSalesRslt.SetColumn(idx,"GREEN_SC_024",dsDivGoalSalesRaw.GetColumn(i,"GREEN_SC_024"));

			if(dsDivGoalSalesRaw.GetColumn(i,"SALES_GOAL_MRA") == 0) idx = dsDivGoalSalesRslt.DeleteRow(idx);
			dsDivGoalSalesRslt.FireEvent = true;
		}

		stProcessRatio.Text = "°æÁÖÀÏ ÁøÇà·ü: "
		    + "°æ·û " +dsTmsProcessRatio.GetColumn(0,"PROCESS_RATIO")
    		+ "% ("+ dsTmsProcessRatio.GetColumn(0,"RACE_DAYS") +"/" + dsTmsProcessRatio.GetColumn(0,"YEAR_RACE_DAYS") + "ÀÏ)"
    		+ ", °æÁ¤ " +dsTmsProcessRatio.GetColumn(1,"PROCESS_RATIO")
    		+ "% ("+ dsTmsProcessRatio.GetColumn(1,"RACE_DAYS") +"/" + dsTmsProcessRatio.GetColumn(1,"YEAR_RACE_DAYS") + "ÀÏ)";

		 tabDivGoalSalesRslt.Click();
    }

}

