function tabDivGoalSalesRslt_OnClick(obj,nButton,nX,nY)
{
	var idx = obj.GetIndex(nX,nY); //X,YÁÂÇ¥¸¦ ÀÌ¿ëÇÏ¿© index¸¦ ¾ò´Â´Ù. 
	if(idx == -1) idx = stabIdx;
	var sTabId = obj.GetItem(idx).Id;

	if( sTabId == "tab1") {
		dsDivGoalSalesRslt.UnFilter(); //ÀüÃ¼
		stabIdx = 0;
	}	
	if( sTabId == "tab2") {
		dsDivGoalSalesRslt.UnFilter();
		dsDivGoalSalesRslt.Filter("MEET_CD=='001'"); //°æ·û
		stabIdx = 1;
	}	 
	if( sTabId == "tab3") {
		dsDivGoalSalesRslt.UnFilter();
		dsDivGoalSalesRslt.Filter("MEET_CD=='003'"); //°æÁ¤
		stabIdx = 2;
	}
}

function fcOnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	var sColId = "";

	if(nCell<0) return;
	sColId = tabDivGoalSalesRslt.tab1.grdDivGoalSalesRsltTot.GetCellProp("body", nCell, "colid");
	var sMeetCd = dsDivGoalSalesRslt.getcolumn(dsDivGoalSalesRslt.Currow,"MEET_CD");
	var sBrncCd = dsDivGoalSalesRslt.getcolumn(dsDivGoalSalesRslt.Currow,"BRNC_CD");
	if(sColId == "MEET_NM" || sColId == "BRNC_NM") {
		if(sMeetCd != "000") {
			var strParam = "RACE_DAY="+calDayEnd.Value;
			strParam +=" MEET_CD="+sMeetCd;
			strParam +=" BRNC_CD="+sBrncCd;
			GBL_RET_VALUE=strParam; // °ª »ý¼º
			global.FrameBottom.fcOpenMdiForm("RSM2111", "»ç¾÷Àåº°¸ÅÃâ¸ñÇ¥´Þ¼ºÇöÈ²", "frm_rsm::RSM2111.xml");
		}
	}
}

