/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
var ARRAY2_GBL_RET_VALUE = Array();
var sBrncCd = "";
function fcFormLoadSetting(obj) {
    sBUTTONLIST   = 'TTFFTFT';     // T초기화,T조회,F저장,F삭제,T엑셀,F인쇄,T닫기
 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

    //공통코드 조회
	fcd_GetComboDs(dsInComCd);
	var sidx = dsBrncCd.AppendRow();
	dsBrncCd.SetColumn(sIdx, "CD","AA");
	dsBrncCd.SetColumn(sIdx, "CD_NM","전체");
	dsBrncCd.MoveRow(sIdx,0);
	dsBrncCd.SetColumn(dsBrncCd.SearchRow("CD_NM=\"광명\""), "CD_NM","경륜본장");
	dsBrncCd.SetColumn(dsBrncCd.SearchRow("CD_NM=\"미사리\""), "CD_NM","경정본장");


	//공통코드 조회
    var sRetValue = decode(GBL_RET_VALUE,null,'',GBL_RET_VALUE);

	if(sRetValue!='')
	{		
		// 이전 페이지에서 파라미터 받음	
		ARRAY2_GBL_RET_VALUE = split2(GBL_RET_VALUE," ","=");
		GBL_RET_VALUE="";
		sBrncCd = fcArray2Value(ARRAY2_GBL_RET_VALUE,  "BRNC_CD");
		rdoMeetCd.Value  = fcArray2Value(ARRAY2_GBL_RET_VALUE,  "MEET_CD");
		cboBrncCdSearch.Value = decode(sBrncCd, "01", "00", sBrncCd);
		calRaceDay.Value = fcArray2Value(ARRAY2_GBL_RET_VALUE,  "RACE_DAY");
	}

	// 화면그리드 리사이즈
	//fcSetResize(this,this.width);
}

