}


/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	var sServiceName = "";

	if(cboBrncCdSearch.Value == "AA") sServiceName = "rsm2111-service:searchDivGoalSalesRsltDetlTot";
	else sServiceName = "rsm2111-service:searchDivGoalSalesRsltDetl";  

	var sInDataSet   = "";
	var sOutDataSet  = "dsDivGoalSalesRsltDetl=dsDivGoalSalesRsltDetl ";     
        sOutDataSet  += "dsTmsProcessRatio=dsTmsProcessRatio "; 

	fcd_AddParam("MEET_CD", rdoMeetCd.Value, );  		//개최장소 
	fcd_AddParam("RACE_DAY", calRaceDay.Value, );  		//경주일 
	fcd_AddParam("BRNC_CD"  , cboBrncCdSearch.Value);	//지점코드
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    if ( sServiceName == "rsm2111-service:searchDivGoalSalesRsltDetl" ) {
		var sRaceDay = substr(calRaceDay.Value,0,4) +"년 "+ substr(calRaceDay.Value,4,2) +"월 "+substr(calRaceDay.Value,6,2) +"일";
		stProcessRatio.Text = "[" + sRaceDay+ "] 경주일 진행률: 경륜 " +dsTmsProcessRatio.GetColumn(0,"PROCESS_RATIO")
    		+"%, 경정 " +dsTmsProcessRatio.GetColumn(1,"PROCESS_RATIO") + "%";
    }

    if ( sServiceName == "rsm2111-service:searchDivGoalSalesRsltDetlTot" ) {
		var sRaceDay = substr(calRaceDay.Value,0,4) +"년 "+ substr(calRaceDay.Value,4,2) +"월 "+substr(calRaceDay.Value,6,2) +"일";
		stProcessRatio.Text = "[" + sRaceDay+ "] 경주일 진행률: 경륜 " +dsTmsProcessRatio.GetColumn(0,"PROCESS_RATIO")
    		+"%, 경정 " +dsTmsProcessRatio.GetColumn(1,"PROCESS_RATIO") + "%";
    }

}

