function tabDivGoalSalesRslt_OnClick(obj,nButton,nX,nY)
{
	var idx = obj.GetIndex(nX,nY); //X,Y좌표를 이용하여 index를 얻는다. 
	var sTabId = obj.GetItem(idx).Id;

	if( sTabId == "tab1") {
		dsDivGoalSalesRslt.UnFilter(); //전체
	}	
	if( sTabId == "tab2") {
		dsDivGoalSalesRslt.UnFilter();
		dsDivGoalSalesRslt.Filter("MEET_CD=='001'"); //경륜
	}	 
	if( sTabId == "tab3") {
		dsDivGoalSalesRslt.UnFilter();
		dsDivGoalSalesRslt.Filter("MEET_CD=='003'"); //경정
	}
}

function fcCboOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
		switch( obj.id ) {
		case "calRaceDay"   :	//경주일자 변경 시
			fcSearch();
			break;
		case "rdoMeetCd"   :	//개최장소 변경 시
			fcSearch();
			break;
		case "cboBrncCdSearch" : //지점변경 시
			fcSearch();
			break;
	}
}

/*-------------------------------------------------------------------------------------------------+
>>  2차원 배열의 변수 값 확인
+-------------------------------------------------------------------------------------------------*/
function fcArray2Value(arr2Param,strParam)
{
	var strResult = "";
	for(var i=0;i<arr2Param.length;i++)
	{	
		for(var j=0;j<arr2Param[i].length;j++)
		{
			if(arr2Param[i][0]==strParam)
			{
				strResult = arr2Param[i][1];
			}
		}
	}
	return strResult;
}


