/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsDIVTMS.ClearData();    

	calRaceDaySt.Value = substr(fcd_GetSysdate("YYYYMMDD"),0,4)+"0101"; // 현재년도의 1월 1일
	calRaceDayEnd.Value	= fcd_GetSysdate("YYYYMMDD");						// 오늘이 기본값	    
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 체크 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    //조회조건 체크및 trim처리

	//기존 데이터셋 초기화
	if (tab0.TabIndex == 0) dsDIVTMS.ClearData();        
	else if (tab0.TabIndex == 1) dsDivMonths.ClearData();        

	return true;
}

