/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
    var sExcelList = "";
    switch (tab0.TabIndex) {
		case 0:
			sExcelList = "tab0.tab1.grdDIVTMS:지점별매출액(회차별)"; //데이터셋 정의
			break;
		case 1:		
			sExcelList = "tab0.tab2.grdDivMonths:지점별매출액(월별)"; //데이터셋 정의
			break;
		case 2:
			sExcelList = "tab0.tab3.grdDIVTMSGsum:지점별매출액(회차별_그린카드포함)"; //데이터셋 정의
			break;
		case 3:		
			sExcelList = "tab0.tab4.grdDivMonthsGsum:지점별매출액(월별_그린카드포함)"; //데이터셋 정의
			break;
    }
    fcd_OpenExcelListExport(sExcelList);
}

