/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {
    dsSellst.ClearData();    
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 체크 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    //조회조건 체크및 trim처리

	fcInit(obj);    //기존 데이터셋 초기화


	if (calRaceDaySt.Value >= "20070101") {
		stHeadInfo.Visible = false;
		grdSellst.SetCellProp("head",1,"text","100~10,000");
		grdSellst.SetCellProp("head",2,"text","10,100~20,000");
		grdSellst.SetCellProp("head",3,"text","20,100~49,900");
		grdSellst.SetCellProp("head",4,"text","50,000");
		grdSellst.SetCellProp("head",5,"text","50,100~80,000");
		grdSellst.SetCellProp("head",6,"text","80,100~99,900");
		grdSellst.SetCellProp("head",7,"text","100,000");
	} else if (calRaceDaySt.Value < "20060101") {
		stHeadInfo.Visible = false;
		grdSellst.SetCellProp("head",1,"text","100~1,000");
		grdSellst.SetCellProp("head",2,"text","1,100~3,000");
		grdSellst.SetCellProp("head",3,"text","3,100~5,000");
		grdSellst.SetCellProp("head",4,"text","5,100~10,000");
		grdSellst.SetCellProp("head",5,"text","10,100~30,000");
		grdSellst.SetCellProp("head",6,"text","30,100~99,900");
		grdSellst.SetCellProp("head",7,"text","50,000");
	} else {
		stHeadInfo.Visible = true;
		grdSellst.SetCellProp("head",1,"text","100~10,000\n(100~1,000)");
		grdSellst.SetCellProp("head",2,"text","10,100~20,000\n(1,100~3,000)");
		grdSellst.SetCellProp("head",3,"text","20,100~49,900\n(3,100~5,000)");
		grdSellst.SetCellProp("head",4,"text","50,000\n(5,100~10,000)");
		grdSellst.SetCellProp("head",5,"text","50,100~80,000\n(10,100~30,000)");
		grdSellst.SetCellProp("head",6,"text","80,100~99,900\n(30,100~99,900)");
		grdSellst.SetCellProp("head",7,"text","100,000\n(50,000)");
	}	

	grdSellst.SetCellProp("head",0,"text", Mid(dsOpt.GetColumn(cboOPT.Index, "col2"),0,2));
	if (Mid(dsOpt.GetColumn(cboOPT.Index, "col2"),0,2) == "지점") {
		grdSellst.setCellProp("body",0,"display","combo");
		grdSellst.setCellProp("body",0,"combocol","CD");
		grdSellst.setCellProp("body",0,"combotext","CD_NM");
		grdSellst.setCellProp("body",0,"combodataset","dsBrncCd");
	} else {
		grdSellst.setCellProp("body",0,"display","text");
	}

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm3110-service:searchSellst01"; // 금액구간별 발매(회차별,전체지점) 
    var sInDataSet   = "";
    var sOutDataSet  = "dsSellst=dsSellst ";

    fcd_AddParam("JOB_TYPE", tab0.TabIndex);	//0:전체,1:현금, 2:그린카드

	fcd_AddParam("MEET_CD", rdoMeetCd.Value);	// 검색 시작일
	fcd_AddParam("GROUP_OPT", cboOPT.Value);	// 옵션	
	fcd_AddParam("RACE_DAY_ST", calRaceDaySt.Value);	// 검색 시작일
	fcd_AddParam("RACE_DAY_END", calRaceDayEnd.Value);	// 검색 종료일
	fcd_AddParam("COMM_NO", cboBrncCd.Value); // 지점코드

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}


