/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    if ( sServiceName == "common-service:searchCode" ) {
		var v_row = dsBrncCd.SearchRow("CD='06'");
		if (v_row > -1) {
			dsBrncCd.DeleteRow(v_row);
		}		
		cboBrncCd.value = gDsUser.GetColumn(0, "BRNC_CD");
		filterBrncCd(rdoMeetCd.Value);
   	}	

	if ( sServiceName == "rsm3110-service:searchSellst01" ) {
	}

	grdSellst.SetFocus();
}

/*-------------------------------------------------------------------------------------------------+
>>  combobox, radio 바뀔 때 이벤트
+-------------------------------------------------------------------------------------------------*/

function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (obj.id == "rdoMeetCd") filterBrncCd(rdoMeetCd.Value);
	fcSearch();
}

function fcOnChar(obj,strPreText,nChar,strPostText,nLLParam,nHLParam)
{
	switch ( obj.ID ) {		
		// 경주 검색 시작 일자에서 ENTER_KEY 눌렀을 경우
		case "calRaceDaySt" :	
			if ( nChar == ENTER_KEY ) {
				fcSearch();
				return;
			}
		break;
		// 경주 검색 마지막 일자에서 ENTER_KEY 눌렀을 경우
		case "calRaceDayEnd" :	
			if ( nChar == ENTER_KEY ) {
				fcSearch();
				return;
			}
		break;

	}
}


// 특정콤포넌트(그리드) 리사이즈
function fcSetResize(obj,nX,nY)
{			
	var nWidth = nX-8;
	var nHeight = nY - tab0.top;
	if( nX <= 820){
		grdSellst.Autofit = false;		
		grdSellst.width = 801;
		tab0.Width = 801;

		Shape1.Width 	= 801;	
		divBtnLst.Left 	= this.width - 393;			
		Static0.Left 	= 725;
		stInfo.left     = 430;
		st_refund_info.left  = 470;
	} else {
		grdSellst.right = nWidth;
		tab0.right = nWidth;

		if( nX > 2400 )	{
			grdSellst.Autofit = true;		
		}else{
			grdSellst.Autofit = false;	
		}

		// 공통
		Shape1.Width 	= nWidth-8;
		divBtnLst.Left 	= this.width - 393;	
		Static0.Left	= this.width - 91;
		stInfo.left     = this.width - stInfo.width - 20;
		st_refund_info.right  = this.width - 75;
	}

	if( nY <= 576){
		grdSellst.Height = 449;
	} else {
		grdSellst.Height = nY - grdSellst.top;
	}
}

// 해당 폼사이즈 변경시
function fcOnSize(obj,nCx,nCy,nState)
{
	fcSetResize(obj,nCx,nCy);
}
function Tab0_OnChanged(obj,nOldIndex,nNewindex)
{
	fcSearch(obj);
	if (nNewIndex == 0) stInfo.Visible = true;
	else                stInfo.Visible = false; 
}

function filterBrncCd(sMeetCd)
{
	var filterStr = "";
	if (sMeetCd == "003") filterStr = "Substr(CD_TERM4,1,1)='1'";
	else                  filterStr = "Substr(CD_TERM4,0,1)='1'";
	dsBrncCd.Filter(filterStr);
}
