	dsMyCat.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 체크 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);
	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
	if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm3180-service:searchMyCat";
    var sInDataSet   = "";
    var sOutDataSet  = "dsMyCat=dsMyCat ";    	

	fcd_AddParam("RACE_DAY_START",  calRaceDaySt.Value);	// 검색 시작일
	fcd_AddParam("RACE_DAY_END", calRaceDayEnd.Value);	// 검색 종료일

	if (chkBranchAll.Value) {
		fcd_AddParam("DIV_NO",    ""); 		// 지점전체.
	} else {
		fcd_AddParam("DIV_NO",    cboBrncCd.Value); 		// 지점코드
	}

	fcd_AddParam("RACE_TYPE",    cboRaceType.Value); 		// 경륜/경정 구분코드
	fcd_AddParam("MEET_CD",     cboMeetCd.Value); 	// 시행처 구분

	if (tab0.TabIndex == 1) {
		sServiceName = "rsm3180-service:searchMyCatSales";
		sOutDataSet  = "dsMyCatSales=dsMyCatSales "; 
		fcd_AddParam("BRNC_CD",     cboBrncCd.Value); 		// 지점코드
		fcd_AddParam("GRN_DIV_NO",  cboGrnDivCd.Value); 	// 투표소코드
	} else if (tab0.TabIndex == 2) {
		sServiceName = "rsm3180-service:searchMyCatTelmp";
		sOutDataSet  = "dsMyCatTelmp=dsMyCatTelmp "; 
	} else if (tab0.TabIndex == 3) {
		sServiceName = "rsm3180-service:searchMyCatVerify";
		sOutDataSet  = "dsMyCatVerify=dsMyCatVerify "; 
	} 
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


