/*-------------------------------------------------------------------------------------------------+
>>  엑셀 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcToExcel(obj) {
	var sExcelList = "";
	if (tab0.TabIndex == 0) {
		sExcelList = "tab0.tab1.grdMyCat:지점별 그린카드 입출금 내역";
	} else if (tab0.TabIndex == 1) {
		sExcelList = "tab0.tab2.grdMyCatSales:투표소별 그린카드매출액";
	} else if (tab0.TabIndex == 2) {
		sExcelList = "tab0.tab3.grdMyCat:단말기별 매출액";
	} else if (tab0.TabIndex == 3) {
		sExcelList = "tab0.tab4.grdMyCat:매출액 검증";
	} 
	fcd_OpenExcelListExport(sExcelList,0);
}


