/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	//Trace("calback:-->"+sServiceName);
    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if(sServiceName == "common-service:searchCode") {
		var nRow = dsBrncCd.FindRow("CD_NM2", gdsUser.GetColumn(0, "BRNC_CD"));
		if (nRow != -1) cboBrncCd.Index = nRow;
		FilterBrncCd();		//투표소 필터링
		dsMeetCd.InsertRow(0);
		dsMeetCd.SetColumn(0, "CD", "%");
		dsMeetCd.SetColumn(0, "CD_NM", "전체");
		cboMeetCd.Index = 0;
	}

	if(sServiceName == "rsm3180-service:searchMyCat")
    {

    }



}


/*-------------------------------------------------------------------------------------------------+
>>  combobox, radio 바뀔 때 이벤트
+-------------------------------------------------------------------------------------------------*/
function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	switch (obj.id) {
		case "cboBrncCd":
			fcSearch();
			FilterBrncCd();
			break;
	}
}

function FilterBrncCd()
{
	dsGrnDivCdfilter.UnFilter();
	if (dsGrnDivCdfilter.RowCount() > 0) {
		var filterStr = "";
		dsGrnDivCdfilter.Filter("CD_TERM4='"+cboBrncCd.Value+"'");  //20141005 김일준  term2->term4
	}
	dsGrnDivCd.copyF(dsGrnDivCdfilter);
	var maxRow = dsGrnDivCd.AddRow();
	dsGrnDivCd.SetColumn(maxRow, "CD", "%");
	dsGrnDivCd.SetColumn(maxRow, "CD_NM", "전체");
	cboGrnDivCd.value = "%";
}

