	 fcSetData(nRow); 

}

function fcTabOnChanged(obj,nOldIndex,nNewindex)
{
	chkBranchAll.Visible = false;
	switch ( nNewindex ) {		
		case 0:	// 지점별 입출금
			chkBranchAll.Visible = true;
			fcSearch(this);
			break;
		case 2:	// 단말기별 발매건수 및 발매금액
			stBrnc.Visible      = true;
			cboBrncCd.Visible   = true;
			cboGrnDivCd.Visible = false;
			stGrnDivCd.Visible  = false;
			fcSearch(this);
			break;

		case 1 : // 투표소별 매출액
			stBrnc.Visible      = true;
			cboBrncCd.Visible   = true;
			cboGrnDivCd.Visible = true;
			stGrnDivCd.Visible  = true;
			fcSearch(this);
			break;		

		case 3:	// 그린카드 매출액 검증
			cboGrnDivCd.Visible = false;
			stGrnDivCd.Visible  = false;
			stBrnc.Visible      = false;
			cboBrncCd.Visible   = false;
			fcSearch(this);
			break;
	}
}



function fcTabOnChanging(obj,nOldIndex,nNewIndex)
{
	if (nNewIndex == 3) {
		if (IsExistVar("sApro", true)) {
			if (sApro == "T") return true;		
		}	
		return false;		
    }
}

function chkBranchAll_OnClick(obj,strValue)
{
	fcSearch();
}
