function fcInit(obj) {
	dsBalance1.ClearData();
	dsBalance2.ClearData();
	dsBalance3.ClearData();
	makeDsBalance4NULL();
    dsTotal.ClearData();
}

function makeDsBalance4NULL(obj) {
    dsBalance4.SetColumn(0,"S_CRA_TO_MRA","");
	dsBalance4.SetColumn(0,"V_CRA_TO_MRA","");
	dsBalance4.SetColumn(0,"S_GASU_BCRC", "");
	dsBalance4.SetColumn(0,"S_GASU_CCRC", "");
	dsBalance4.SetColumn(0,"S_MIJI_AMT_CRA", "");
	dsBalance4.SetColumn(0,"S_MIJI_AMT_MRA", "");

	dsBalance4.SetColumn(1,"S_CRA_TO_MRA","");
	dsBalance4.SetColumn(1,"V_CRA_TO_MRA","");
	dsBalance4.SetColumn(1,"S_GASU_BCRC", "");
	dsBalance4.SetColumn(1,"S_GASU_CCRC", "");
	dsBalance4.SetColumn(1,"S_MIJI_AMT_CRA", "");
	dsBalance4.SetColumn(1,"S_MIJI_AMT_MRA", "");
}

