function fcToExcel(obj) {
    var sExcelList = "grdBalance1:2"; 
		sExcelList += "|grdBalance2:2"; 
		sExcelList += "|grdBalance3:2"; // :숫자는 + head +summary+ 하단 띄우고 싶은 사이 간격
		sExcelList += "|grdBalance4:1"; // :숫자는 + head +summary+ 하단 띄우고 싶은 사이 간격

	fnc_OpenMultiExcelExport(sExcelList,"밸런스("+calRaceDay.Value+")");
}

