/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	if ( sServiceName == "rsm4041-service:searchRaceInfo" )
	{
	}
		dsBalance1.Copy(dsTotal);
		dsBalance2.Copy(dsTotal);
		dsBalance3.Copy(dsTotal);

		dsBalance1.Filter("TYPE_NO==1");
		dsBalance2.Filter("TYPE_NO==2");
		dsBalance3.Filter("TYPE_NO==3");
		// 경주권 구매권

		var MEET_CD	= dsRaceInfo.GetColumn(0,"MEET_CD");
		var MEET_NM = dsRaceInfo.GetColumn(0,"MEET_NM");

		grdBalance4.SetCellProp("head",0,"text",MEET_NM+"경주일");

		// 경륜 --->경정
		dsSCRAToMRA.Copy(dsTotal);
		dsSCRAToMRA.Filter("TYPE_NO==1)&&(SELL_CD=='01'||SELL_CD=='02'||SELL_CD=='04')");

		dsVCRAToMRA.Copy(dsTotal);
		dsVCRAToMRA.Filter("TYPE_NO==2)&&(SELL_CD=='01'||SELL_CD=='02'||SELL_CD=='04')");

		var nS_cra_to_mra = dsSCRAToMRA.Sum("BOAT_AMT");
		var nV_cra_to_mra = dsVCRAToMRA.Sum("BOAT_AMT");
		dsBalance4.SetColumn(0,"S_CRA_TO_MRA",nS_cra_to_mra);
		dsBalance4.SetColumn(0,"V_CRA_TO_MRA",nV_cra_to_mra);

		// 경륜 <--- 경정
		dsSMRAToCRA.Copy(dsTotal);
		dsSMRAToCRA.Filter("TYPE_NO==1&&(SELL_CD=='03'||SELL_CD=='08'||SELL_CD=='09')");

		dsVMRAToCRA.Copy(dsTotal);
		dsVMRAToCRA.Filter("TYPE_NO==2&&(SELL_CD=='03'||SELL_CD=='08'||SELL_CD=='09')");

		var nS_mra_to_cra = dsSMRAToCRA.Sum("CYCLE_AMT");
		var nV_mra_to_cra = dsVMRAToCRA.Sum("CYCLE_AMT");

		dsBalance4.SetColumn(1,"S_CRA_TO_MRA",nS_mra_to_cra);
		dsBalance4.SetColumn(1,"V_CRA_TO_MRA",nV_mra_to_cra);

		dsBalance4.SetColumn(0, "S_GITA_AMT1", "");
		dsBalance4.SetColumn(0, "S_GITA_AMT2", "");
		dsBalance4.SetColumn(0, "S_GASU_BCRC", "");
		dsBalance4.SetColumn(0, "S_GASU_CCRC", "");
		dsBalance4.SetColumn(1, "S_GITA_AMT1", "");
		dsBalance4.SetColumn(1, "S_GITA_AMT2", "");
		dsBalance4.SetColumn(1, "S_GASU_BCRC", "");
		dsBalance4.SetColumn(1, "S_GASU_CCRC", "");

		if(MEET_CD == "003") {
			//미지급금(경주권)경륜
			dsSMRAToCRA.Copy(dsTotal);
			dsSMRAToCRA.Filter("TYPE_NO==1&&SELL_CD=='01'");
			var n_gita_amt1 = dsSMRAToCRA.Sum("GITA_AMT1");
			var n_gita_amt2 = dsSMRAToCRA.Sum("GITA_AMT2");
			dsBalance4.SetColumn(0,"S_MIJI_AMT_CRA",n_gita_amt1+n_gita_amt2); //미지급금(경주권)경륜

			//미지급금(경주권)경정
			dsSMRAToCRA.Copy(dsTotal);
			dsSMRAToCRA.Filter("TYPE_NO==1&&(SELL_CD=='03'||SELL_CD=='09')");
			n_gita_amt1 = dsSMRAToCRA.Sum("GITA_AMT1");
			n_gita_amt2 = dsSMRAToCRA.Sum("GITA_AMT2");
			dsBalance4.SetColumn(0,"S_MIJI_AMT_MRA",n_gita_amt1+n_gita_amt2);

			//가수금(부산) 경정경주일
			dsSMRAToCRA.Copy(dsTotal);
			dsSMRAToCRA.Filter("TYPE_NO==1&&SELL_CD=='04'");
			var n_gita_amt1 = dsSMRAToCRA.Sum("GITA_AMT1");
			var n_gita_amt2 = dsSMRAToCRA.Sum("GITA_AMT2");
			dsBalance4.SetColumn(0,"S_GASU_BCRC",n_gita_amt1+n_gita_amt2);

			//가수금(창원) 경정경주일
			dsSMRAToCRA.Copy(dsTotal);
			dsSMRAToCRA.Filter("TYPE_NO==1&&SELL_CD=='02'");
			var n_gita_amt1 = dsSMRAToCRA.Sum("GITA_AMT1");
			var n_gita_amt2 = dsSMRAToCRA.Sum("GITA_AMT2");
			dsBalance4.SetColumn(0,"S_GASU_CCRC",n_gita_amt1+n_gita_amt2);
		}
		if(MEET_CD == "001") {
			//미지급금(경주권)경륜
			dsSMRAToCRA.Copy(dsTotal);
			dsSMRAToCRA.Filter("TYPE_NO==1&&SELL_CD=='01'");
			var n_gita_amt1 = dsSMRAToCRA.Sum("GITA_AMT1");
			var n_gita_amt2 = dsSMRAToCRA.Sum("GITA_AMT2");
			dsBalance4.SetColumn(1,"S_MIJI_AMT_CRA",n_gita_amt1+n_gita_amt2); //미지급금(경주권)경륜

			//미지급금(경주권)경정
			dsSMRAToCRA.Copy(dsTotal);
			dsSMRAToCRA.Filter("TYPE_NO==1&&(SELL_CD=='03'||SELL_CD=='09')");
			n_gita_amt1 = dsSMRAToCRA.Sum("GITA_AMT1");
			n_gita_amt2 = dsSMRAToCRA.Sum("GITA_AMT2");
			dsBalance4.SetColumn(1,"S_MIJI_AMT_MRA",n_gita_amt1+n_gita_amt2);

			//가수금(부산) 경륜경주일
			dsSMRAToCRA.Copy(dsTotal);
			dsSMRAToCRA.Filter("TYPE_NO==1&&SELL_CD=='04'");
			var n_gita_amt1 = dsSMRAToCRA.Sum("GITA_AMT1");
			var n_gita_amt2 = dsSMRAToCRA.Sum("GITA_AMT2");
			dsBalance4.SetColumn(1,"S_GASU_BCRC",n_gita_amt1+n_gita_amt2);

			//가수금(창원) 경륜경주일
			dsSMRAToCRA.Copy(dsTotal);
			dsSMRAToCRA.Filter("TYPE_NO==1&&SELL_CD=='02'");
			var n_gita_amt1 = dsSMRAToCRA.Sum("GITA_AMT1");
			var n_gita_amt2 = dsSMRAToCRA.Sum("GITA_AMT2");
			dsBalance4.SetColumn(1,"S_GASU_CCRC",n_gita_amt1+n_gita_amt2);
		}	
}

/*-------------------------------------------------------------------------------------------------+
>>  평일환급 엔터 처리
+-------------------------------------------------------------------------------------------------*/
function fcOnChar(obj,strPreText,nChar,strPostText,nLLParam,nHLParam)
{
	switch ( obj.ID ) {		
		// 경주일자에서 ENTER_KEY 눌렀을 경우
		case "calRaceDay" :	
			if ( nChar == ENTER_KEY ) {
				fcSearchInfo();	
				return;
			}
		break;
	}

}
