    dsPCBalance.ClearData();
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 체크 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    //조회조건 체크및 trim처리

	fcInit(obj);    //기존 데이터셋 초기화

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/

function fcSearch(obj) {
    var strParam =" STND_YEAR="+cboStndYear.Value;			

	GBL_RET_VALUE=strParam; // 값 생성	
	global.FrameBottom.fcOpenMdiForm("RSM4050", "밸런스조회", "frm_rsm::RSM4050.xml");

}


/*-------------------------------------------------------------------------------------------------+
>>  내용 검색
+-------------------------------------------------------------------------------------------------*/
function fcSearchInfo(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm4051-service:searchPCBalance"; // 경륜 조회

    var sInDataSet   = "";
    var sOutDataSet  = "dsPCBalance=dsPCBalance ";		
		sOutDataSet  += "dsRaceInfo=dsRaceInfo ";		    

    // TMS ,DAY_ORD	전페이지 파라미터
    fcd_AddParam("STND_YEAR", cboStndYear.Value);				// 기준년도
	fcd_AddParam("RACE_DAY", calRaceDay.Value );				// 경주일

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}


// 년도 변경
function fcOnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	fcSearch();	
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 저장가능유무처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {  

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsPCBalance)&&!fnc_DatasetChangeCheck(dsIW) ) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}	
	if( !fnc_Message(SNIS_CRA_B999, "Y","밸런스 입력을 하시겠습니까?")) return; 

	return true;

}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

	// IW 구매권 dsPCBalance 로 옮김

	dsPCBalance.Copy(dsPCBalanceTmp);

	dsPCBalance.AddRow();
	dsPCBalance.SetColumn(dsPCBalance.currow,"RACE_DAY",dsIWTmp.GetColumn(0,"RACE_DAY"));
	dsPCBalance.SetColumn(dsPCBalance.currow,"TYPE_NO",dsIWTmp.GetColumn(0,"TYPE_NO"));
	dsPCBalance.SetColumn(dsPCBalance.currow,"TYPE_NM",dsIWTmp.GetColumn(0,"TYPE_NM"));
	dsPCBalance.SetColumn(dsPCBalance.currow,"SELL_CD",dsIWTmp.GetColumn(0,"SELL_CD"));
	dsPCBalance.SetColumn(dsPCBalance.currow,"SELL_NM",dsIWTmp.GetColumn(0,"SELL_NM"));
	dsPCBalance.SetColumn(dsPCBalance.currow,"NOTYET_TOT_AMT",dsIWTmp.GetColumn(0,"NOTYET_TOT_AMT"));
	dsPCBalance.SetColumn(dsPCBalance.currow,"END_AMT",dsIWTmp.GetColumn(0,"END_AMT"));
	dsPCBalance.SetColumn(dsPCBalance.currow,"GITA_AMT1",dsIWTmp.GetColumn(0,"GITA_AMT1"));
	dsPCBalance.SetColumn(dsPCBalance.currow,"GITA_AMT2",dsIWTmp.GetColumn(0,"GITA_AMT2"));
	dsPCBalance.SetColumn(dsPCBalance.currow,"NOTYET_AMT",dsIWTmp.GetColumn(0,"NOTYET_AMT"));
	dsPCBalance.SetColumn(dsPCBalance.currow,"CYCLE_AMT",dsIWTmp.GetColumn(0,"CYCLE_AMT"));
	dsPCBalance.SetColumn(dsPCBalance.currow,"BOAT_AMT",dsIWTmp.GetColumn(0,"BOAT_AMT"));
	dsPCBalance.SetColumn(dsPCBalance.currow,"IW_AMT",dsIWTmp.GetColumn(0,"IW_AMT"));



    if ( !fcSaveCheck() ) return;
    var sServiceName = "rsm4051-service:saveBalanceInsert";
    var sInDataSet   = "dsPCBalance=dsPCBalance:a ";
    var sOutDataSet  = "";    

	fcd_AddParam("RACE_DAY", calRaceDay.Value);

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}




