/*-------------------------------------------------------------------------------------------------+
>>  밸런스 삭제
+-------------------------------------------------------------------------------------------------*/
function fcDelBalance(obj,nX,nY)
{
	if( fnc_Message( SNIS_COM_1004, "Y", "밸런스") ) {

		var sServiceName = "rsm4051-service:deleteBalance";
		var sInDataSet   = "";
		var sOutDataSet  = "";    

		fcd_AddParam("MODE", "D");
		fcd_AddParam("RACE_DAY", calRaceDay.Value);	
		fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

	}
}



/***************************************************************************************************
*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;


	if ( sServiceName == "rsm4051-service:searchPCBalance" )
	{
		// 새로 입력해야 할 때
		if(dsPCBalance.rowcount==0)
		{
			for(var i=0;i<dsPCBalanceTmp.rowcount;i++)
			{
				dsPCBalanceTmp.SetColumn(i,"RACE_DAY",calRaceDay.Value);
			}			

			for(var i=0;i<dsIWTmp.rowcount;i++)
			{
				dsIWTmp.SetColumn(i,"RACE_DAY",calRaceDay.Value);
			}				
		}	
		else
		{
			// 기존에 있는 내용 불러 올 때
			dsPCBalanceTmp.Copy(dsPCBalance);
			dsPCBalanceTmp.Filter("TYPE_NO != 3");
			// IW 구매권
			dsIWTmp.Copy(dsPCBalance);
			dsIWTmp.Filter("TYPE_NO == 3");// IW 구매권
		}
		stRaceInfo.Text = dsRaceInfo.GetColumn(0,"MEET_NM_DESC");

		return;
	}
	else if ( sServiceName == "rsm4051-service:deleteBalance" )
	{
		fcSearch();
	}

}


/*-------------------------------------------------------------------------------------------------+
>>  엔터 조회 처리
+-------------------------------------------------------------------------------------------------*/
function fcOnChar(obj,strPreText,nChar,strPostText,nLLParam,nHLParam)
{
	switch ( obj.ID ) {		
		// 경주일자에서 ENTER_KEY 눌렀을 경우
		case "calRaceDay" :	
			if ( nChar == ENTER_KEY ) {
				fcSearchInfo();	
				return;
			}
		break;
	}
}
