function fcSaveCheck(obj) {
	return true;
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {
	if ( !fcSaveCheck() ) return;

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	sServiceName = "rsm1010-service:saveLISFileUpload";
	sInDataSet   = "dsLISFile=dsLISFile:u ";
	sOutDataSet  = "";

	if(fcDsCheck(0)) //  LIS File
	{
		var arrFile = fnc_FileWrite(  divBtnLst.HttpFile , dsLISFile.GetColumn(0, "FILE_LOCAL_PATH" ), dsLISFile.GetColumn(0, "FILE_NM" ) ,"lis"); 	
		if ( arrFile[0] != "SUCC" ) 
		{
			alert(arrFile[1] + " : LIS File 업로드 에러발생");
			return false;
		}
		dsLISFile.SetColumn(0,"FILE_PATH",arrFile[1]);

		dsRaceDayFileInfo.SetColumn(dsRaceDayFileInfo.currow,"FILE_PATH",arrFile[1]);	
		dsRaceDayFileInfo.SetColumn(dsRaceDayFileInfo.currow,"FILE_NM",dsLISFile.GetColumn(0, "FILE_NM"));	
	}

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
}

