}


/*-------------------------------------------------------------------------------------------------+
>>  프린트 인쇄 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj) {

	//데이터셋을 초기화 한다.
	ds_reportParam.DeleteAll();
	var nRnum;
	var RACE_DAY = dsRaceDayFileInfo.GetColumn(dsRaceDayFileInfo.row , "RACE_DAY");
	var DT=substr(RACE_DAY,4,4);

    // 필드 값을 셋팅한다.
    var paramField = [
							"STND_YEAR"							// 경주년도(년도)
						,	"RACE_DAY"							// 경주일자(년월일)
						,	"DT"								// 경주일자(월일)
					 ];

	// 값을 셋팅한다.
	var paramValue = [
							dsRaceDayFileInfo.GetColumn(dsRaceDayFileInfo.row, "STND_YEAR" )
						,	dsRaceDayFileInfo.GetColumn(dsRaceDayFileInfo.row, "RACE_DAY" )
						,	DT
					 ];


	var rowCnt = paramField.length;	
	for(var row = 0 ; row < rowCnt; row++)
	{
		nRnum = ds_reportParam.AppendRow();
		ds_reportParam.SetColumn(nRnum, "PARAM_NM", paramField[row]);
		ds_reportParam.SetColumn(nRnum, "PARAM_VAL",paramValue[row]);
	}

	/* 레포트 배율 추가 - 시작 */
	iRow = ds_reportParam.AddRow();

	// Default Scale 파라미터명 추가(고정)
	ds_reportParam.SetColumn(iRow, "PARAM_NM", "DEFAULT_SCALE");
	ds_reportParam.SetColumn(iRow, "PARAM_VAL", "1");

	//레포트 트랙잭션을 태운다.
	fnc_ReportPreView("rsm4060", ds_reportParam.ID, "N");

}


