/*-------------------------------------------------------------------------------------------------+
>>  그리드 클릭 했을 때 이벤트
+-------------------------------------------------------------------------------------------------*/
function fcGrdOnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	switch(obj.id)
	{
		case "grdLISFile":
			if (nCell == 1) fcFileRead("notepad.exe");
			break;

		default :	
			fcInit(obj);
			fcSetData(nRow); 
			break;		
	}

}

//조회내역 클릭이 아닌 키보드 이동일 경우 클릭과 같은 이벤트 발생
function fcGrdOnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	switch ( obj.id ) 
	{	
		case "grdRaceInfo" : // pc 파일 업로드일 경우 파일을 저장한다
			fcGrdOnCellClick(obj, nRow);

			break;
	}

}

/*-------------------------------------------------------------------------------------------------+
>>  파일 다운받기
+-------------------------------------------------------------------------------------------------*/

function fcGrdOnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	switch ( obj.id ) 
	{	
		case "grdLISFile" : // pc 파일 업로드일 경우 파일을 저장한다
			if (nCell == 0) fcFileRead("");

			break;
	}
}
function fcFileRead(viewer) 
{
	var sLocalFileName  = fcd_GetSelectFile("Save", dsLISFile.GetColumn(0, "FILE_NM")); // 파일명
	var sServerFileName = dsLISFile.GetColumn(0, "FILE_PATH");	// 파일 경로
	var sFileSize       = 0;
	if ( sLocalFileName == null ) return;

	fnc_FileRead(divBtnLst.HttpFile, sLocalFileName[0] + "\\" + sLocalFileName[1], sServerFileName, sFileSize, 0, "PROG_BAR", dsLISFile, viewer);
}

function fcdsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.id ) 
	{	
		case "dsCntntListSum" :
			if (dsCntntListSum.RowCount() > 3 ) {
				grdCntntListSum.Height = 113;
			} else {
				grdCntntListSum.Height = 95;
			}
			break;
	}
}

/*-------------------------------
실입금합계액 구하기 
----------------------------------*/

function fcFillSilInSum(i)
{

	var myCatID;
	var rtnAmt;

	if (dsMyCat.GetColumn(i,"DIV_NO") =="00") {
		rtnAmt = dsCntntListSum.GetColumn(dsCntntListSum.FindRow("BRNC_CD","00"),"NET_INCOME") + dsMyCat.GetColumn(i,"SIL_IN_AMT");	
	}
	else {
		//map 에서 id 검색
		myCatID =dsDivMap.GetColumn( dsDivMap.FindRow("CD",dsMyCat.GetColumn(i,"DIV_NO")), "CD_NM2");
		rtnAmt = dsCntntList.GetColumn(dsCntntList.FindRow("BRNC_CD",myCatID),"NET_INCOME") + dsMyCat.GetColumn(i,"SIL_IN_AMT");	
	}
	return rtnAmt;	
}
