/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    // 조회 완료 후
    if ( sServiceName == "rsm5060-service:searchPcGita" ) {
		stCntChkValid.Text = "(일치 "+dsCntChkValid.GetColumn(0,"CNT_CHK_VALID_T")+"건, 불일치 "+dsCntChkValid.GetColumn(0,"CNT_CHK_VALID_F")+"건)";
        fcBtnControl();
    }

	// DB입력
    if ( sServiceName ==  "rsm5060-service:saveDbGitaFile") {
        var arr = split2(GBL_RET_VALUE,"^","=");
        if( arr[0][1] != "O"){
			fnc_Message(SNIS_CRA_B999, "N","DB입력이 완료되었습니다!");	
			fcSearch();	
		}	
    }
    // 확정
    if ( sServiceName == "rsm5060-service:saveGitaCfmCntnt" ) {
		var arr = split2(GBL_RET_VALUE,"^","=");

		GBL_RET_VALUE = "";	// 사용한 GBL_RET_VALUE 변수는 초기화 시켜줌 다른 페이지 이동 할 때 들고감

		var RESULT	= fnc_Array2Value(arr,"RESULT"); // 결과

//        if( arr[0][1] != "O"){
		if( RESULT == "T"){
			fnc_Message(SNIS_CRA_B999, "N","확정이 완료되었습니다!");
			fcSearch();
		}	
    }
    // 확정취소
    if ( sServiceName == "rsm5060-service:deleteGitaCfmCntnt" ) {
        fnc_Message(SNIS_CRA_B999, "N","확정취소가 완료되었습니다!");
		fcSearch();
    }

}

