/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

    //Return Key Press時 Next Component focus
    if ( nChar == ENTER_KEY ) {
        var oFocusComponent = GetNextComponent(true);
        oFocusComponent.setfocus();
        return;
    }
    //Control Key가 눌려져 있다면 단축키 처리
    //if (bControl) fnc_HotKey(this, nChar);
}

// 확정/ 확정취소버튼을 컨트롤한다.
function fcBtnControl()
{
	// 기확정을 확인한다.
	http.Sync = true;

	var sServiceName = "rsm5060-service:searchCfmCnt";
	var sInDataSet   = "";
	var sOutDataSet  = "dsCnt=dsCnt ";    

	//서버로 보낼 값
	fcd_AddParam("PAY_YEAR" , cboPayYear.Value);
	fcd_AddParam("PAY_MM"   , cboPayMm.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

	http.Sync = false;
	if( toInteger(dsCnt.GetColumn(0,"CFMCNT")) > 0){
		 cfmBtn.Enable = false;
		 btnCfmCancel.Enable = true;
	}else{
		 cfmBtn.Enable = true;
		 btnCfmCancel.Enable = false;
	}
}


function fcBtnOnClick(obj) {       
    switch(obj.id) {
        case "dbBtn":  //DB입력

			var sServiceName = "rsm5060-service:saveDbGitaFile";
			var sInDataSet   = " ";
			var sOutDataSet  = " ";  	

			//서버로 보낼 값
			fcd_AddParam("FILE_PATH", dsFilePath.GetColumn(dsFilePath.currow, "CD_NM"));
			fcd_AddParam("PAY_YEAR" , cboPayYear.Value);
			fcd_AddParam("PAY_MM"   , cboPayMm.Value);

			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true, "U");
			break;

		case "cfmBtn":  //확정
			//변경된 자료가 있는지 체크 => 변경된 자료가 있으면 작업 중지
            if ( fnc_DatasetChangeCheck(dsPcGita) ) {
                fnc_Message(SNIS_COM_1052, "N");
                return;
            }


            if( toInteger(dsCntChkValid.GetColumn(0,"CNT_CHK_VALID_T")) < 1) {

                fnc_Message(SNIS_COM_1053, "N");
                return;
            }


            // 기확정을 확인한다.
            http.Sync = true;

            var sServiceName = "rsm5060-service:searchCfmCnt";
			var sInDataSet   = "";
			var sOutDataSet  = "dsCnt=dsCnt ";    

			//서버로 보낼 값
			fcd_AddParam("PAY_YEAR" , cboPayYear.Value);
			fcd_AddParam("PAY_MM"   , cboPayMm.Value);

			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

            http.Sync = false;
            if( toInteger(dsCnt.GetColumn(0,"CFMCNT")) > 0){
				 fnc_Message(SNIS_CRA_B999, "N","기 확정건 입니다!");
				 return;
            }

            if(fnc_Message(SNIS_RBM_D003, "Y")) {           
                var sServiceName = "rsm5060-service:saveGitaCfmCntnt";
                var sInDataSet   = "dsPcGita=dsPcGita:a ";
                var sOutDataSet  = "dsPcGita=dsPcGita ";    

				//서버로 보낼 값
				fcd_AddParam("PAY_YEAR", cboPayYear.Value);
				fcd_AddParam("PAY_MM"  , cboPayMm.Value);   
				fcd_AddParam("MEET_CD" , cboMeetCdSearch.Value);
				fcd_AddParam("SELL_CD" , cboSellCdSearch.Value);

                fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);
            }
			break;
		case "btnCfmCancel":	// 확정취소

			if( toInteger(dsCnt.GetColumn(0,"CFMCNT")) < 1){
				fnc_Message(SNIS_COM_1053, "N");
                return;
            }


			if( !fnc_Message(SNIS_CRA_B999, "Y", cboPayYear.Value+"년"+cboPayMm.Value+"월분 확정취소를 하시겠습니까?")) return; 
			var sServiceName = "rsm5060-service:deleteGitaCfmCntnt";
			var sInDataSet   = "";
			var sOutDataSet  = "";    

			//서버로 보낼 값
			fcd_AddParam("PAY_YEAR" , cboPayYear.Value);
			fcd_AddParam("PAY_MM"   , cboPayMm.Value);

			fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);			
			break;
	}
}
function dsBrncCd_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if ( nReason == 0){
		obj.fireEvent = false;
		var nRow  = obj.addrow();
		obj.SetColumn(nRow,"CD","01");
		obj.SetColumn(nRow,"CD_NM","광명");

		nRow  = obj.addrow();
		obj.SetColumn(nRow,"CD","02");
		obj.SetColumn(nRow,"CD_NM","광명");

		nRow  = obj.addrow();
		obj.SetColumn(nRow,"CD","03");
		obj.SetColumn(nRow,"CD_NM","광명");

		nRow  = obj.addrow();
		obj.SetColumn(nRow,"CD","04");
		obj.SetColumn(nRow,"CD_NM","광명");

		nRow  = obj.addrow();
		obj.SetColumn(nRow,"CD","08");
		obj.SetColumn(nRow,"CD_NM","광명");

		obj.fireEvent = true;
	}
}


// 특정콤포넌트(그리드) 리사이즈
function fcSetResize(obj,nX)
{	
	var arrObj = array(-1);
	arrObj[0] = "grdPcGita";


	var nWidth = nX-8;
	if( nX <= 820){
		for( var i=0; i<length(arrObj); i++){
			object(arrObj[i]).Autofit = false;		
			object(arrObj[i]).width = 801;
			object(arrObj[i]).Redraw;
		}
		Shape1.Width = 801;	
		divBtnLst.Left = this.width - 393;			
		//btnCfmCancel.Left = this.width - 114;
		return;
	}

	for( var i=0; i<length(arrObj); i++){

		object(arrObj[i]).right = nWidth;
		if( i == 0){
			if( nX < 1120) object(arrObj[i]).Autofit = false;		
			else object(arrObj[i]).Autofit = true;		
		}else{
			object(arrObj[i]).Autofit = true;		
		}
	}	

	// 공통
	Shape1.Width = nWidth-8;
	divBtnLst.Left = this.width - 393;	
	//btnCfmCancel.Left = this.width - 114;
}

// 해당 폼사이즈 변경시
function fcOnSize(obj,nCx,nCy,nState)
{

	fcSetResize(obj,nCx);

}
