/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

    //if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsm6040-service:searchTsnRcpt";
    var sInDataSet   = "";
    var sOutDataSet  = "dsTsnRcptResult=dsTsnRcptResult ";

	fcd_AddParam("rcptstatcd"	, cbRcptStatCd.Value);
	fcd_AddParam("brnccd"		, decode(cbBrncCd.Value,'00','^(0[012348])$',cbBrncCd.Value) );
	fcd_AddParam("custnm"		, edtCustNm.Text);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}

