/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

    if( dsTsnRcptResult.row < 0) return false;
	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsTsnRcptResult) ) {
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	} 

	if( !fnc_Message(SNIS_COM_1012, "Y")) return;

    var sServiceName = "rsm6040-service:saveTsnRcpt";
    var sInDataSet   = "dsTsnRcptResult=dsTsnRcptResult:u ";
    var sOutDataSet  = "";

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


