/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {
	var ARRAY2_GBL_RET_VALUE = "";
    var sResult              = "";
    var tmpStrDesc           = "";

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    if( sServiceName == "rsm6040-service:searchTsnRcpt"){		
		if(  dsTsnRcptResult.rowcount > 0) {
			dsTsnRcptResult.row = -1;
			dsTsnRcptResult.row = 0;	
		}
    }
    // 접수 처리 후
    if( sServiceName == "rsm6040-service:saveReq"){		
		tmpStrDesc ="경주권 분실신고가 접수 되었습니다.";
		fcSendSms(tmpStrDesc);

		fnc_Message(SNIS_CRA_B999, "N","접수처리가 완료되었습니다!");
		cbRcptStatCd.Value = "002";
		fcSearch();
    }
    // 접추취소 처리 후
    if( sServiceName == "rsm6040-service:saveReqCancel"){	
    	ARRAY2_GBL_RET_VALUE = split2(GBL_RET_VALUE,"^","=");
        sResult = fnc_Array2Value(ARRAY2_GBL_RET_VALUE, "RESULT"); // 결과

		if( sResult == "S" ) {
			fnc_Message(SNIS_CRA_B999, "N","접수취소 처리가 완료되었습니다!");
			cbRcptStatCd.Value = "001";
		}
		fcSearch();
    }
    // 기환급 처리 후
    if( sServiceName == "rsm6040-service:saveRefund"){	
    	tmpStrDesc ="죄송합니다. 이미 환급처리된 경주권 입니다.";
		fcSendSms(tmpStrDesc);

		fnc_Message(SNIS_CRA_B999, "N","기환급 처리가 완료되었습니다!");
		cbRcptStatCd.Value = "004";
		fcSearch();
    }
    // 해당없음 처리 후
    if( sServiceName == "rsm6040-service:saveRcptNo"){	
    	tmpStrDesc ="경주권 확인이 불가하니 다시한번 경주권 내용을 확인하시기 바랍니다.";
		fcSendSms(tmpStrDesc);

		fnc_Message(SNIS_CRA_B999, "N","해당없음 처리가 완료되었습니다!");
		cbRcptStatCd.Value = "003";
		fcSearch();
    }
    // 자료존재 처리 후
	if( sServiceName == "rsm6040-service:saveDataEx"){
		//MISS_REPO_NO
		var sMissRepoNo = dsTsnRcptResult.GetColumn(dsTsnRcptResult.row,"MISS_REPO_NO");


	    tmpStrDesc ="고객님의 분실경주권(구매권)이 확인되어 처리중입니다."+ chr(10);
		tmpStrDesc += "분실신고번호 : "+ sMissRepoNo;


		fcSendSms(tmpStrDesc);

		fnc_Message(SNIS_CRA_B999, "N","자료존재 처리가 완료되었습니다!");
		cbRcptStatCd.Value = "005";
		fcSearch();
    }      
}

