function dsTsnRcptResult_OnRowPosChanged(obj,nOldRow,nRow)
{
	// 접수상태에 따라 버튼제어
	switch(dsTsnRcptResult.getcolumn(nRow,"RCPT_STAT_CD"))
	{
		case "001" : 	// 고객등록
			btnRcpt.Enable			= true; 	// 접수
			btnRcptCancel.Enable	= false; 	// 접수취소					
			btnRefund.Enable		= false;	// 기환급
			btnx.Enable 			= false;	// 해당없음
			//btnDataEx.Enable		= false;	// 자료존재	
			break;
		case "002" :	// 접수
			btnRcpt.Enable			= false; 	// 접수
			btnRcptCancel.Enable	= true; 	// 접수취소					
			btnRefund.Enable		= true;		// 기환급
			btnx.Enable 			= true;		// 해당없음
			//btnDataEx.Enable		= true;		// 자료존재	

		  break;		
	  default :		
			btnRcpt.Enable			= false; 	// 접수
			btnRcptCancel.Enable	= true; 	// 접수취소					
			btnRefund.Enable		= true;		// 기환급
			btnx.Enable 			= true;		// 해당없음
			//btnDataEx.Enable		= true;		// 자료존재	
		 break;	
	}
}

// 접수처리
function btnRcpt_OnClick(obj)
{
	if( dsTsnRcptResult.row < 0) return;

	if( !fnc_Message(SNIS_CRA_B999, "Y","접수 처리를 하시겠습니까?")) return; 
	dsTsnRcptResult_S.ClearData();
	dsTsnRcptResult_S.AddRow();
	dsTsnRcptResult_S.CopyRow(0,"dsTsnRcptResult",dsTsnRcptResult.row);	
	dsTsnRcptResult_S.SetColumn(0,"RCPT_STAT_CD","002");	// 접수상태로

	var sServiceName = "rsm6040-service:saveReq";
	var sInDataSet   = "dsTsnRcptResult_S=dsTsnRcptResult_S ";
	var sOutDataSet  = "";  	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

