// 본장지점목록에 전체 추가
function dsBrncCd_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	if( nReason == 0){
		obj.fireEvent = false;
		obj.insertrow(0);
		obj.setColumn(0,"CD","");
		obj.setColumn(0,"CD_NM","전체");		
		obj.fireEvent = true;
		cbBrncCd.Index = 0;
	}
}

//sms발송
function fcSendSms(strDesc)
{

	if( ! fnc_IsNull( dsTsnRcptResult.GetColumn(dsTsnRcptResult.row,"CUST_PHONE_NO") ) ){
		fcd_SendSms(	  "02-2067-5000"   							// 송신자 번호
						, gdsUser.GetColumn(0,"USER_ID")			// 송신자 사번 
						, gdsUser.GetColumn(0,"USER_NM")			// 송신자 성명
						, dsTsnRcptResult.GetColumn(dsTsnRcptResult.row,"CUST_PHONE_NO")		// 수신자 번호
						, dsTsnRcptResult.GetColumn(dsTsnRcptResult.row,"CUST_NM")	// 수신자명
						, "[분실신고서 온라인 접수]" 									// 제목   
						, dsTsnRcptResult.GetColumn(dsTsnRcptResult.row,"CUST_NM")										// 수신자아이디);
					    , strDesc 									// 문자내용(80Bytes이하)   
					);

	}
}


function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	switch ( obj.id ) {
				case "dsBrncCd" :

					if ( nReason == 0){
						obj.fireEvent = false;
						var nRow  = obj.addrow();
						obj.SetColumn(nRow,"CD","01");
						obj.SetColumn(nRow,"CD_NM","광명");

						nRow  = obj.addrow();
						obj.SetColumn(nRow,"CD","02");
						obj.SetColumn(nRow,"CD_NM","광명");

						nRow  = obj.addrow();
						obj.SetColumn(nRow,"CD","03");
						obj.SetColumn(nRow,"CD_NM","광명");

						nRow  = obj.addrow();
						obj.SetColumn(nRow,"CD","04");
						obj.SetColumn(nRow,"CD_NM","광명");

						nRow  = obj.addrow();
						obj.SetColumn(nRow,"CD","08");
						obj.SetColumn(nRow,"CD_NM","광명");

						obj.fireEvent = true;
					}
					break;

				case "dsBrncCdSearch" :
					    if( nReason == 0){
							obj.fireEvent = false;
							obj.insertrow(0);
							obj.setColumn(0,"CD","");
							obj.setColumn(0,"CD_NM","전체");		
							obj.fireEvent = true;
							cbBrncCd.Index = 0;
						}
					break;

	 }
}
