/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 체크 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

   	var strFromDate = tabExpiry.Tab1.calFromDate.Value;		// 발행시작일자
    var strToDate	= tabExpiry.Tab1.calToDate.Value;		// 발행종료일자    

   	if( fnc_IsNull(strFromDate) ){			
		fnc_Message(SNIS_COM_1035, "N","발행기간일자");
		tabExpiry.Tab1.calFromDate.DropDown();
		return false;
	}
	if( fnc_IsNull(strToDate) ){			
		fnc_Message(SNIS_COM_1035, "N","발행기간일자");
		tabExpiry.Tab1.calToDate.DropDown();
		return false; 
	}
	if( !fnc_IsDate(strFromDate) ){			
		fnc_Message(SNIS_CRA_F007, "N");
		tabExpiry.Tab1.calFromDate.DropDown();
		return false;
	}
	if( !fnc_IsDate(strToDate) ){			
		fnc_Message(SNIS_CRA_F007, "N");
		tabExpiry.Tab1.calToDate.DropDown();
		return false;
	}
	if( strFromDate > strToDate ){

		fnc_Message(SNIS_CRA_B005, "N","조회");
		tabExpiry.Tab1.calToDate.DropDown();
		return false;
	}
	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {

	if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";
    var strBrnc = "";    

	if( tabExpiry.TabIndex == 0){ 
		strBrnc =  tabExpiry.Tab1.cbBrnc.Value;


		strBrnc =  decode(strBrnc,"00","^(0[012348])$",strBrnc);
		// 만기도래
		sServiceName = "rsm6060-service:searchTsnExpiry";
		sInDataSet   = "";
		sOutDataSet  = "dsTsnRcptResult=dsTsnRcptResult ";

		fcd_AddParam("tsnno"	, tabExpiry.Tab1.edtTsnNo.Text );
		fcd_AddParam("brnccd"	, strBrnc);
		fcd_AddParam("custnm"	, tabExpiry.Tab1.edtCustNm.Text );
		fcd_AddParam("fromdate"	, tabExpiry.Tab1.calFromDate.Value );
		fcd_AddParam("todate"	, tabExpiry.Tab1.calToDate.Value );
		if (tabExpiry.tab1.chkAcept.value == true) { // 접수자료만 조회시
			fcd_AddParam("statcd"	, "001" );
		} else {
			fcd_AddParam("statcd"	, "000" );
		}



	}else{ 
		strBrnc =  tabExpiry.Tab2.cbbrnc.Value;

		strBrnc =  decode(strBrnc,"00","^(0[012348])$",strBrnc);
		// 시효만료
		sServiceName = "rsm6060-service:searchTsnExpiryEnd";
		sInDataSet   = "";
		sOutDataSet  = "dsTsnRcptResultEnd=dsTsnRcptResultEnd ";

		fcd_AddParam("tsnno"	, tabExpiry.Tab2.edtTsnNo.Text );
		fcd_AddParam("brnccd"	, strBrnc);		
		fcd_AddParam("custnm"	, tabExpiry.Tab2.edtCustNm.Text );

		fcd_AddParam("fromdate"		, tabExpiry.tab2.calFromDate.Value );
		fcd_AddParam("todate"		, tabExpiry.tab2.calToDate.Value);



	}
    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);


}


