*                                      사용자 정의  처리 부분                                      *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction 후 처리 해야 할 내용!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

   	if( sServiceName == "common-service:searchSysdate"){
		var strHdate="";
		var nCnt = 0;
		var startDt;
		var endDt;
		if(dsDate.rowcount > 0){
			dsExpi.fireEvent = false;
			strHdate = dsDate.GetColumn(0,"YYYYMMDD");
			dsExpi.ClearData();
			var nRow;
			for(var i=1; i<15;i++){
				nCnt = (350 + i)*(-1);
				if( i == 1 ) startDt = addDate(strHdate,nCnt);
				if( i == 14 ) endDt = addDate(strHdate,nCnt);
				nRow = dsExpi.AddRow();
				dsExpi.SetColumn(nRow,"CD"		,addDate(strHdate,nCnt));				
				dsExpi.SetColumn(nRow,"CD_NM"	,toString(addDate(strHdate,nCnt),"@@@@-@@-@@"));
			}
			dsExpi.fireEvent = true;
			tabExpiry.tab1.cbExpi.Index = 0;
			tabExpiry.tab1.stcdate.Text = toString(endDt,"@@@@/@@/@@") +" ~ "+toString(startDt,"@@@@/@@/@@");

			tabExpiry.tab1.calFromDate = endDt;
			tabExpiry.tab1.calToDate = startDt;

		}
   	}

   	if( sServiceName == "rsm6060-service:saveCnclReq"){   	
		fcSearch();
   	}

}

