function fcDsOnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{	

		switch ( obj.id ) {
				case "dsBrncCd" :

					if ( nReason == 0){
						obj.fireEvent = false;
						var nRow  = obj.addrow();
						obj.SetColumn(nRow,"CD","01");
						obj.SetColumn(nRow,"CD_NM","광명");

						nRow  = obj.addrow();
						obj.SetColumn(nRow,"CD","02");
						obj.SetColumn(nRow,"CD_NM","광명");

						nRow  = obj.addrow();
						obj.SetColumn(nRow,"CD","03");
						obj.SetColumn(nRow,"CD_NM","광명");

						nRow  = obj.addrow();
						obj.SetColumn(nRow,"CD","04");
						obj.SetColumn(nRow,"CD_NM","광명");

						nRow  = obj.addrow();
						obj.SetColumn(nRow,"CD","08");
						obj.SetColumn(nRow,"CD_NM","광명");

						obj.fireEvent = true;
					}
					break;

				case "dsBrncCdSearch" :
					dsBrncCdSearch.InsertRow(0);
					dsBrncCdSearch.SetColumn(0, "CD_NM", "전체");
					tabExpiry.tab1.cbbrnc.index = 0;
					tabExpiry.tab2.cbbrnc.index = 0;
					break;	
					break;

	 }
}

function tabExpiry_Tab1_grdResult_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
 if(obj.GetCellPos() == 3){
		if((bCtrl) && (nChar == 17))
		{
			ClearClipboard();
			SetClipBoard("CF_TEXT",obj.GetCellText("body",dsTsnRcptResult.row,obj.GetCellPos()));
		}
   }
}

