		}
   }
}

// 해지요청
function btnCnclReq_OnClick(obj)
{
	if( !fnc_Message(SNIS_CRA_B999, "Y","해지요청 처리를 하시겠습니까?")) return; 

	dsTsnRcptResult_S.SetColumn(0,"TIC_STAS","003");	// 해지요청상태로
	dsTsnRcptResult_S.SetColumn(0,"CNCL_REQ_DEPT",gdsUser.GetColumn(0,"TEAM_NM") );	// 해지요청자 부서
	dsTsnRcptResult_S.SetColumn(0,"CNCL_REQ_ID"	 ,gdsUser.GetColumn(0,"USER_ID") );	// 해지요청자 ID

	var sServiceName = "rsm6060-service:saveCnclReq";
	var sInDataSet   = "dsTsnRcptResult_S=dsTsnRcptResult_S ";
	var sOutDataSet  = "";  	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


function dsTsnRcptResult_OnRowPosChanged(obj,nOldRow,nRow)
{

    dsTsnRcptResult_S.ClearData();
	if( obj.row < 0) return;
	var nRow = dsTsnRcptResult_S.AddRow();	
	dsTsnRcptResult_S.CopyRow(nRow,"dsTsnRcptResult",dsTsnRcptResult.row);

	switch(dsTsnRcptResult_S.getcolumn(nRow,"TIC_STAS"))
	{
		case "001" : 	// 지불정지
			btnCnclReq.Enable		= false;	// 해지요청
			tabExpiry.Tab1.btnCnclReqCancel.Enable		= false;	// 해지요청 취소
			break;
		case "002" :	// 접수			
			btnCnclReq.Enable		= true;		// 해지요청
			tabExpiry.Tab1.btnCnclReqCancel.Enable		= false;	// 해지요청 취소
		  break;
		case "003" :	// 해지요청			
			btnCnclReq.Enable		= false;	// 해지요청
			tabExpiry.Tab1.btnCnclReqCancel.Enable		= true;	// 해지요청 취소
		  break;	 
	    case "004" :	// 해지			
			btnCnclReq.Enable		= false;	// 해지
			tabExpiry.Tab1.btnCnclReqCancel.Enable		= false;	// 해지요청 취소
		  break;	  
	    case "005" :	// 환급 			
			btnCnclReq.Enable		= false;	// 환급
			tabExpiry.Tab1.btnCnclReqCancel.Enable		= false;	// 해지요청 취소
		  break;
	    default :			  

	}
}

function btnCnclReqCancel_OnClick(obj)
{

	if( !fnc_Message(SNIS_CRA_B999, "Y","해지요청 취소처리를 하시겠습니까?")) return; 
	dsTsnRcptResult_S.SetColumn(0,"TIC_STAS","002");	// 접수상태로
	dsTsnRcptResult_S.SetColumn(0,"CNCL_REQ_DEPT","");	// 해지요청자 부서
	dsTsnRcptResult_S.SetColumn(0,"CNCL_REQ_ID"	 ,"");	// 해지요청자 ID

	var sServiceName = "rsm6060-service:saveCnclReq";
	var sInDataSet   = "dsTsnRcptResult_S=dsTsnRcptResult_S ";
	var sOutDataSet  = "";  	

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}
