function fcAddRow(obj) {
	//if( LEVEL == "2" ) {
		var nRow = dsPcTeller.AddRow();
		dsPcTeller.SetColumn(nRow, "PROC_GBN"     , "002");	
		dsPcTeller.SetColumn(nRow, "APRV_STAS"    , "001");	
		dsPcTeller.SetColumn(nRow, "ORG_APRV_STAS", "001");	
	//} else {
	//	fnc_Message(SNIS_RBM_D011, "N");
	//}
}

/*-------------------------------------------------------------------------------------------------+
>>  Çà»èÁ¦ ¹öÆ° Å¬¸¯ ãÁ Ã³¸®
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	if( LEVEL == "1" ) {
		// CheckÇÑ Ç×¸ñÁß, Ã³¸®¿©ºÎ°¡ »èÁ¦°¡ ¾Æ´Ñ °ªÀÌ ÀÖ´Ù¸é »èÁ¦ ºÒ°¡´É
		for( var i=0; i < dsPcTeller.rowcount; i++) {
			if( dsPcTeller.GetColumn(i, "CHK") == "1" ) {
				if( dsPcTeller.GetColumn(i, "PROC_GBN") != "004" ) {
					fnc_Message(SNIS_CRA_B999, "N", "Ã³¸®¿©ºÎ°¡ [ »èÁ¦ ]ÀÎ Ç×¸ñ¿¡ ´ëÇØ¼­¸¸ »èÁ¦°¡ °¡´ÉÇÕ´Ï´Ù.");
					return;
				}
			}
		}

		if ( !fnc_DeleteCheck(dsPcTeller, "CHK", "1") ) return;
    } else if( LEVEL == "2" ) {
		if ( !fnc_DeleteCheck(dsPcTeller, "CHK", "1") ) return;
	} else if( LEVEL == "3" ) {
		fnc_Message(SNIS_RBM_D011, "N");
	}
}

/***************************************************************************************************
*                          CALL BACK Ã³¸® ºÎºÐ                                                     *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  Transaction ÈÄ Ã³¸® ÇØ¾ß ÇÒ ³»¿ë!
+-------------------------------------------------------------------------------------------------*/
function fcCallBack(sServiceName, ErrorCode, ErrorMSG) {

	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	// Á¶È¸ ¿Ï·á ÈÄ
	if ( sServiceName == "rsm9010-service:searchPcTeller" ) {
		if( sApro != "T" ) {
			if (dsPcTeller.SearchRow("APRV_STAS='003'") >= 0) {
				btnReconfirm.Visible = true;
			} else {
				btnReconfirm.Visible = false;
			}
		}		

	}			

	// ÀúÀå ¿Ï·á ÈÄ
	if ( sServiceName == "rsm9010-service:saveTeller" ) {
		var ARRAY2_GBL_RET_VALUE = split2(GBL_RET_VALUE,"^","=");
        var sResult = fnc_Array2Value(ARRAY2_GBL_RET_VALUE, "RESULT"); // °á°ú

		if( sResult == "S" ) {		// ¼º°ø½Ã¿¡¸¸ ÂÊÁö ¹ß¼Û
			if( SEND_GBN == "R" ) {	// ¹ß¸Å¿ø°ü¸® ¿äÃ»
				fcSendMngMsgReq(); 
			} else if(SEND_GBN == "A" || SEND_GBN == "C") {	// ¹ß¸Å¿ø°ü¸® ½ÂÀÎ
				fcSendEmp(SEND_GBN);          
			}
		}
		fcSearch();
	}
}

//ÄÃ·³ º¯°æ½Ã
function fcDsOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {

	if( strColumnID == "PROC_GBN" ) {
		if( obj.GetRowType(nRow) != "insert" &&
			varNewValue == "002"             ) {
			obj.SetColumn(nRow, strColumnID, "001");
			fnc_Message(SNIS_RBM_D010, "N");
		}
	}
}


//±×¸®µå ÆË¾÷¹öÆ°
function fcGrdOnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex) {

	var sFormId 	= "frm_rsm::RSM9011.xml";
	var sArgs 		= "sCommNo="+quote(cboCommNo.Value);
	var arrReturn   = array(6);

	if (nCell == 3 ) {
		arrReturn = Dialog(sFormId, sArgs, 334, 443, "");

		if ( length(arrReturn) > 0  ) {

			dsPcTeller.SetColumn(nRow, "COMM_NO"     , arrReturn[0]);
			dsPcTeller.SetColumn(nRow, "COMM_NM"     , arrReturn[1]);
			dsPcTeller.SetColumn(nRow, "EMP_NO"      , arrReturn[2]);
			dsPcTeller.SetColumn(nRow, "TELLER_NM"   , arrReturn[3]);
			dsPcTeller.SetColumn(nRow, "SELL_TYPE_CD", arrReturn[4]);
			dsPcTeller.SetColumn(nRow, "TELLER_GBN"  , arrReturn[5]);
		}
	}
}

//ÂÊÁö Àü¼Û_¿äÃ»ÀÚ°¡ ½ÂÀÎÀÚ¿¡°Ô
function fcSendMngMsgReq(sTitle, sDesc){
