	// ¼Û½ÅÀÚ »ç¹ø, ¼ö½ÅÀÚ »ç¹ø, Á¦¸ñ, È­¸éID, ³»¿ë, ±¸ºÐ°ª
	fcd_SendMessage(sSendUser, sRecvUser, sTitle, "RSM9010",  sDesc, "comSendMsg");									
}

//ÂÊÁö ´ë»óÀÚ Setting 
function fcEmpSet() {
	var sEmpNo = "";
	var nAddRow;
	var nFindRow;

	dsSendEmp.ClearData();
	for(var i=0; i < dsPcTeller.rowcount; i++) {
		if( dsPcTeller.GetColumn(i, "CHK") == "1" ) {
			//sEmpNo   = dsPcTeller.GetColumn(i, "EMP_NO");	
			sEmpNo   = dsPcTeller.GetColumn(i, "REQ_EMP_NO");		// »ç¿ëÀÚ°¡ ¾Æ´Ñ ¿äÃ»ÀÚ·Î º¯°æ(2012.04.26)
			nFindRow = dsSendEmp.FindRow("EMP_NO", sEmpNo);

			if( nFindRow == -1 ) {
				nAddRow = dsSendEmp.AddRow();
				dsSendEmp.SetColumn(nAddRow, "EMP_NO", sEmpNo);
				dsSendEmp.SetColumn(nAddRow, "CNT"   , 1);
			} else {
				dsSendEmp.SetColumn(nFindRow, "CNT", dsSendEmp.GetColumn(nFindRow, "CNT") + 1);
			}
		}
	}

	for(var i=0; i< dsPcTeller.GetDelRowCount(); i++) {
		//sEmpNo   = dsPcTeller.GetDelColumn(i, "EMP_NO");
		sEmpNo   = dsPcTeller.GetColumn(i, "REQ_EMP_NO");		// »ç¿ëÀÚ°¡ ¾Æ´Ñ ¿äÃ»ÀÚ·Î º¯°æ(2012.04.26)
		nFindRow = dsSendEmp.FindRow("EMP_NO", sEmpNo);

		if( nFindRow == -1 ) {
			nAddRow = dsSendEmp.AddRow();
			dsSendEmp.SetColumn(nAddRow, "EMP_NO", sEmpNo);
			dsSendEmp.SetColumn(nAddRow, "CNT"   , 1);
		} else {
			dsSendEmp.SetColumn(nFindRow, "CNT", dsSendEmp.GetColumn(nFindRow, "CNT") + 1);
		}
	}
}

//½ÂÀÎ,¹Ý·Á ÂÊÁö º¸³»±â( sGbn   A;½ÂÀÎ   C:¹Ý·Á  )
function fcSendEmp(sGbn) {
	var sComment = "";

	if( sGbn == "A" ) {
		sComment = "½ÂÀÎ";
	} else {
		sComment = "¹Ý·Á";
	}

	for(var i=0; i < dsSendEmp.rowcount; i++) {
		var sRecvUser = dsSendEmp.GetColumn(i, "EMP_NO");
		var sTitle    = "¹ß¸Å¿ø°ü¸®"+ sComment + "Ã³¸®";
