}


/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	// DataSet의 Header 정보가 있을 때  Add 처리
	switch ( obj.GetForm().id ) {
		case "grpBtnRow" :
			if ( !fnc_DeleteCheck(dsGrpCd, "chk", "1") ) return;
			dsGrpCd_OnRowPosChanged();
			break;
		case "specBtnRow" :
			if ( !fnc_DeleteCheck(dsSpecCd, "chk", "1") ) return;
			break;
	}
}


