// 특정콤포넌트(그리드) 리사이즈
function fcSetResize(obj,nX)
{	// 해당화면은 기준사이즈보다 Height가 크기때문에 스크롤바 생성발생.그에 따라 그리드사이즈 조정
	var arrObj = array(-1);
	arrObj[0] = "grdSpecCd";

	var nWidth = nX-28;

	if( nX <= 820){
		grdSpecCd.Autofit = false;		
		grdSpecCd.Width = 467;
		specBtnRow.left = 771;
		return;
	}else{

		if( nX < 1200) grdSpecCd.AutoFit = false;
		else grdSpecCd.AutoFit = true;
		grdSpecCd.Width = nWidth - grdSpecCd.left;
	}	


	// 공통
	Shape1.Width = nWidth-8;
	divBtnLst.Left = this.width - 410;	
	specBtnRow.left = nWidth-40;

}
