function lFcPrint(url, parameters, DataSetId) 
{
	if(parameters.Trim().length() > 0){
		if(G_ds_param.colcount > 0) G_ds_param.Clear();

		var parameterArray = parameters.split(";");
		if(parameterArray.Length() > 0){
			G_ds_param.AddColumn("PARAM_NM");
			G_ds_param.AddColumn("PARAM_VAL");

			for(var i=0; i<parameterArray.Length(); i++) {
				var keyValueArray = parameterArray[i].Split("=");
				var aRow = G_ds_param.AddRow();
				G_ds_param.SetColumn(aRow, "PARAM_NM" ,keyValueArray[0]);
				G_ds_param.SetColumn(aRow, "PARAM_VAL",keyValueArray[1]);
			}
		}
	}

	G_ds_return.Clear();
	G_ds_return.addColumn("UBISQLID");
	G_ds_return.addColumn("DataSetId");
	G_ds_return.addRow();
	G_ds_return.setColumn(0,"UBISQLID", "SQL");
	G_ds_return.setColumn(0,"DataSetId", datasetId);

	/* 레포트 배율 추가 - 시작 */
	var nRow = G_ds_param.AddRow();
	// Default Scale 파라미터명 추가(고정)
	G_ds_param.SetColumn(nRow, "PARAM_NM", "DEFAULT_SCALE");
	// Scale 값 추가(PARAM_VAL = 1 : 100 + 1 * 20 = 120%, PARAM_VAL = 2 : 100 + 2 * 20 = 140%, PARAM_VAL = 3 : 100 + 3 * 20 = 160%.....) 
	G_ds_param.SetColumn(nRow, "PARAM_VAL", "1");
	/* 레포트 배율 추가 - 끝 */

	lFcReportPreView(url, G_ds_param.ID);

}


/**
 * 레포트 미리보기 팝업 오픈(GateWay 방식 - SQL을 레포트에 포함)
 *
 * param rptFile   - 레포트 파일(ID)
 * param dsParamId - 파라미터 정의 Dataset ID
 * param fullSize  - 팝업 호출시 Screen Size에 꽉찬 상태로 호출할것인지 여부
 * return void
 */
function lFcReportPreView(rptFile, paramDsId, fullSize)
{
	var nWidth = 1024;
	var nHeight = 715;
	var nPosX = 0;
	var nPosY = 0;
	var bFullSize;

	var fSQLCallFlag;

    if(rptFile==null || fnc_isNull(rptFile)) {

        lFcAlert(L_COM_ALT_0004, "rptFile");

        return;
    }

    if (fullSize == null) {
        bFullSize = false;
    } else {
        bFullSize = true;
    }

    if (fullSize) {
        nWidth  = getDeviceInfo("CXScreen") - 8;
        nHeight = getDeviceInfo("CYScreen") - 55;
        nPosX = (getDeviceInfo("CXScreen")-nWidth-6)/2;
        nPosY = (getDeviceInfo("CYScreen")-nHeight-56)/2;
    }

    var sArg = "rptFile=" + quote(rptFile);

    if(fnc_IsNotNull(paramDsId)) {

        sArg += " paramDsId=" + quote(paramDsId);
    }

	sArg += " fSQLCallFlag=true";
    var bRtn = Open("common::comVrtRptViewer_IfisTest.xml", sArg, nWidth, nHeight, "AutoSize=false Resize=true", nPosX, nPosY);

    return;
}


/*-------------------------------------------------------------------------------------------------+
>>  인쇄
+-------------------------------------------------------------------------------------------------*/
function fcPrint(obj)
{
			//데이터셋을 초기화 한다.
	ds_reportParam.DeleteAll();


	// 필드 값을 셋팅한다.
	var paramField = [
							"MEET_CD"						// 시행처코드 

					 ];

	// 값을 셋팅한다.
	var paramValue = [
							rdoMeetCd.Value
					 ];


	var rowCnt = paramField.length;	
	for(var row = 0 ; row < rowCnt; row++)
	{
		nRnum = ds_reportParam.AppendRow();
		ds_reportParam.SetColumn(nRnum, "PARAM_NM", paramField[row]);
		ds_reportParam.SetColumn(nRnum, "PARAM_VAL",paramValue[row]);
	}

	/* 레포트 배율 추가 - 시작 */
	iRow = ds_reportParam.AddRow();

	// Default Scale 파라미터명 추가(고정)
	ds_reportParam.SetColumn(iRow, "PARAM_NM", "DEFAULT_SCALE");
	ds_reportParam.SetColumn(iRow, "PARAM_VAL", "1");

	//레포트 트랙잭션을 태운다.
	fnc_ReportPreView("rsm0000", ds_reportParam.ID, "N");

}
