function fcInit(obj) {
    dsMenuGrpValue.ClearData();
    dsMenuGrpList.ClearData();

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 체크 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {
    //조회조건 체크및 trim처리
	fcInit(obj);    //기존 데이터셋 초기화	
	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj) {
    if ( !fcSearchCheck(obj) ) return;

	var sServiceName = "rsy2010-service:searchMenuGrpValue";
    var sInDataSet   = "";
    var sOutDataSet  = "dsMenuGrpValue=dsMenuGrpValue ";
        sOutDataSet += "dsMenuGrpList=dsMenuGrpList ";    

	var sMnGrpCd = "";
	if (length(cboMnGrpCd.Value) != 7) {
        sMnGrpCd = "R000000";
    }else{    
        sMnGrpCd=cboMnGrpCd.Value;
    }

	fcd_AddParam("MN_GRP", sMnGrpCd);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

/*-------------------------------------------------------------------------------------------------+
>>  메뉴그룹에 메뉴 클릭했을 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchMenuList(obj,nOldRow,nRow) {
    if ( dsMenuGrpList.GetRowType(nRow) == "insert" ) {
        edMenuId.Readonly = false;
	} else {
        edMenuId.Readonly = true;
	}	

    if (trim(dsMenuGrpValue.GetColumn(nRow, "URL")) == "" || trim(dsMenuGrpValue.GetColumn(nRow, "URL")) == null) {              //메뉴리스트조회
        var sServiceName = "rsy2010-service:searchMenuGrpList";
        var sInDataSet   = "";
        var sOutDataSet  = "dsMenuGrpList=dsMenuGrpList";    

        fcd_AddParam("MN_ID", dsMenuGrpValue.GetColumn(nRow, "MN_ID"));

	    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);        
    }else{
        dsMenuGrpList.ClearData();
    }
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 저장가능유무처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {  
    var sMandatoryColID = "";
    var sMandatoryColName = "";

	//변경된 자료가 있는지 체크
	if ( !fnc_DatasetChangeCheck(dsMenuGrpList) ) {
		//저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");	
		return false;
	}	

	// 필수항목체크
	oDataSet = dsMenuGrpList;

	//선택된 메뉴그룹이 2레벨일경우는 화면ID, URL 필수입력이니까 이부분 추가체크하자
	if (dsMenuGrpValue.getColumn(dsMenuGrpValue.row, "LVL") == 2) {
        sMandatoryColID   = "MN_ID|MN_NM|SCRN_ID|URL|MN_LEVEL";
        sMandatoryColName = "메뉴ID|메뉴명|화면ID|URL|정렬순서";
    } else {
        sMandatoryColID   = "MN_ID|MN_NM|MN_LEVEL";
        sMandatoryColName = "메뉴ID|메뉴명|정렬순서";
    }    
	oGrid             = grdMnList;

	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
	    return false;
	}

	return true;

}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

    if ( !fcSaveCheck() ) return;

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    

    sServiceName = "rsy2010-service:saveMenu";
    sInDataSet   = "dsMenuGrpList=dsMenuGrpList:u ";

    sOutDataSet  = "";

	fcd_AddParam(Trim("DEL_YN"), "N"); 
	//fcd_AddParam("GRP_NM", edSearchTxt.text);

    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}


