/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	//DataSet의 Header 정보가 있을 때  Add 처리	  
    dsMenuGrpList.addRow();
    var nRow = dsMenuGrpList.currow;
    dsMenuGrpList.setColumn(nRow, "UP_MN_ID", dsMenuGrpValue.getColumn(dsMenuGrpValue.row, "MN_ID"));
    dsMenuGrpList.SetColumn(nRow, "UP_MN_NM", dsMenuGrpValue.getColumn(dsMenuGrpValue.row, "MN_NM"));

    //메뉴레벨이 0레벨이 아니면 메뉴그룹을 자동세팅해준다.(변경도불가)
    if (dsMenuGrpValue.getColumn(dsMenuGrpValue.row, "LVL") == 0 && dsMenuGrpValue.getColumn(dsMenuGrpValue.row, "MN_LEVEL") == '000000') {
        //1레벨 첫번째 행 추가일 경우(발생될 확률 거의 없지만...)
        if (nRow == 0) {
            dsMenuGrpList.SetColumn(nRow, "ORD_NO", 20);
        } else {
            var nOrd = dsMenuGrpList.getColumn(nRow-1, "ORD_NO");
            //add이면 98보다 크면 안된다.  insert에서는 다음 row보다는 크면 안된다.
            if ( nOrd > 98 ) {
                fnc_Message(SNIS_RBM_Z001, "SERVERMSG");	
                dsMenuGrpList.DeleteRow(nRow);
                return false;
            } else {
                dsMenuGrpList.SetColumn(nRow, "ORD_NO", nOrd+1);
            }

        }        
    } else {
        dsMenuGrpList.SetColumn(nRow, "ORD_NO", dsMenuGrpValue.getColumn(dsMenuGrpValue.row, "ORD_NO"));
    }    

    if ( dsMenuGrpList.GetRowType(nRow) == "insert" ) {
        edMenuId.Readonly = false;
	} else {
        edMenuId.Readonly = true;
	}	

    edMenuId.SetFocus();
}

/*-------------------------------------------------------------------------------------------------+
>>  행삽입 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInsRow(obj) {
    dsMenuGrpList.InsertRow(dsMenuGrpList.GetCurrow());           //Record Ins
    var nRow = dsMenuGrpList.currow;
    dsMenuGrpList.setColumn(nRow, "UP_MN_ID", dsMenuGrpValue.getColumn(dsMenuGrpValue.row, "MN_ID"));
    dsMenuGrpList.SetColumn(nRow, "UP_MN_NM", dsMenuGrpValue.getColumn(dsMenuGrpValue.row, "MN_NM"));

    //메뉴레벨이 0레벨이 아니면 메뉴그룹을 자동세팅해준다.(변경도불가)
    if (dsMenuGrpValue.getColumn(dsMenuGrpValue.row, "LVL") == 0 && dsMenuGrpValue.getColumn(dsMenuGrpValue.row, "MN_LEVEL") == '000000') {
        if (nRow == 0) {
            dsMenuGrpList.SetColumn(nRow, "ORD_NO", dsMenuGrpList.getColumn(nRow+1, "ORD_NO")-1);
        } else {
            var nOrd = dsMenuGrpList.getColumn(nRow-1, "ORD_NO");
            //add이면 98보다 크면 안된다.  insert에서는 다음 row보다는 크면 안된다.
            nOrd += 1;
            if ( nOrd >= dsMenuGrpList.getColumn(nRow+1, "ORD_NO") ) {
                fnc_Message(SNIS_RBM_Z002, "SERVERMSG");	
                dsMenuGrpList.DeleteRow(nRow);
                return false;
            } else {
                dsMenuGrpList.SetColumn(nRow, "ORD_NO", nOrd);
            }

        }        
    } else {
        dsMenuGrpList.SetColumn(nRow, "ORD_NO", dsMenuGrpValue.getColumn(dsMenuGrpValue.row, "ORD_NO"));
    }    

    if ( dsMenuGrpList.GetRowType(nRow) == "insert" ) {
        edMenuId.Readonly = false;
	} else {
        edMenuId.Readonly = true;
	}	
}

