    if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

    // 메뉴리스트 조회 완료 후rsy2010-service:searchMenuGrpList
	if ( sServiceName == "rsy2010-service:searchMenuGrpList" ) {

        if (edGrpMnAfterLev.Text == "R000000") {

        }else{

        }
        return;  
	}


	if ( sServiceName == "rsy2010-service:saveMenu" ) {

		fcSearch();
	}


}

