function fcInit() {

    //dsGrpCodeListValue.ClearData();
    dsUser.ClearData();

}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 체크 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck() {
    //조회조건 체크및 trim처리

	fcInit();    //기존 데이터셋 초기화

	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch() {
    if ( !fcSearchCheck() ) return;

    var sServiceName = "rsy3010-service:searchUser";
    var sInDataSet   = "";
    var sOutDataSet  = "dsUser=dsUser ";

	fcd_AddParam("USER_NM", edSearchTxt.text);
	fcd_AddParam("TEAM_CD", cboTeamNm.Value);

	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}

/*-------------------------------------------------------------------------------------------------+
>>  부서 목록 조회 처리
+-------------------------------------------------------------------------------------------------*/
function fcGetDeptTree() {
	var sServiceName = "common-service:searchOrgan";           // 서버에 요청할 Sevice 명
	var sInDataSet   = "";                                  // Input DataSet 명칭
	var sOutDataSet  = "dsOutDept=dsOutDept";                 // Output DataSet 명칭

	fcd_AddParam("DEPT_ID", 'GA120000');
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcbGetDeptTree", false);
}



function fcbGetDeptTree(sServiceName, ErrorCode, ErrorMSG) {
	if ( !fnc_result(ErrorCode, ErrorMSG) ) return;

	tbAdmin.tbDept.tbDept.ExpandItem(0);


}
/*-------------------------------------------------------------------------------------------------+
>>  사용자클릭시 사용자메뉴권한 리스트 조회
+-------------------------------------------------------------------------------------------------*/
function fcSearchAuthMenuList(obj) {



    var sServiceName = "rsy3010-service:searchMenuAuth";
    var sInDataSet   = "";
    var sOutDataSet  = "dsMenuAuth=dsMenuAuth ";

    var objDs;

	if(tbAdmin.TabIndex == 0){
		objDs = dsOutDept;
	}else if(tbAdmin.TabIndex == 1){
		objDs = dsUser;
	}


	var nRow = objDs.currow;

	fcd_AddParam("USER_ID", objDs.GetColumn(nRow, "USER_ID"));


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}


function fcSearchAuthMenuListByGrd(obj) {



    var sServiceName = "rsy3010-service:searchMenuAuth";
    var sInDataSet   = "";
    var sOutDataSet  = "dsMenuAuth=dsMenuAuth ";

    var objDs;

	objDs = dsUser;

	var nRow = objDs.currow;

	fcd_AddParam("USER_ID", objDs.GetColumn(nRow, "USER_ID"));


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 저장가능유무처리
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) { 
	var objDs;

	if(tbAdmin.TabIndex == 0){
		objDs = dsOutDept;
	}else if(tbAdmin.TabIndex == 1){
		objDs = dsUser;
	}


	//선택된 Row Count
	var iSelectRowCount = fnc_GetColumnValueCount(objDs, "CHK", "1");

	if ( iSelectRowCount <= 0 ) {
        fnc_Message(SNIS_CRA_Z003, "N");
        return false;
	}

	return true;

}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

    if ( !fcSaveCheck() ) return;

    var sServiceName = "";
    var sInDataSet   = "";
    var sOutDataSet  = "";    


    var sDsId;

	if(tbAdmin.TabIndex == 0){
		sDsId = "dsOutDept";
	}else if(tbAdmin.TabIndex == 1){
		sDsId = "dsUser";
	}


    sServiceName = "rsy3010-service:saveAdminInfo";
    sInDataSet   = "dsUser="+sDsId+":u ";
    sInDataSet  += "dsMenuAuth=dsMenuAuth:u ";

    sOutDataSet  = "";



    fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

}


