function dsOutMNGrp_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{    
    dsOutMNGrp.InsertRow(0);
	dsOutMNGrp.SetColumn(0, "MN_NM", " ");

	dsOutMNGrp.InsertRow(1);
	dsOutMNGrp.SetColumn(1, "MN_NM", "전체");
	cboMnGrpCd.index = 0;
}

function dsOutTeamNm_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	dsOutTeamNm.InsertRow(0);
	dsOutTeamNm.SetColumn(0, "TEAM_NM", "전체");
	cboTeamNm.index = 0;
}


function chk_OnClick(obj,strValue)
{
    for ( var i = 0; i < dsMenuAuth.rowcount; i++ ) {
        if(cboMnGrpCd.Index == 1) {                                                 //전체이면...
            fcCommonChk(obj, i);
        } else {
            if(cboMnGrpCd.Value == dsMenuAuth.getColumn(i, "UPUP_MN_ID")) {    //선택메뉴일때...
                fcCommonChk(obj, i);     
            } 
        }

	}
}

function fcCommonChk(obj, i)
{
    if(obj.id == "chkAuth" && obj.value == 0) {              //조회 언체크하면 저장,승인 자동 언체크
        dsMenuAuth.setColumn(i, "CHK", 0);
    } else if(obj.id != "chkAuth" && obj.value == 1) {       //저장,승인 체크하면 조회부분 자동체크되도록
        dsMenuAuth.setColumn(i, "CHK", chkAuth.Value);
    } else {    
		dsMenuAuth.setColumn(i, "CHK", chkAuth.Value);
    }
}


//메뉴명 셀을 클릭하면 해당 메뉴의 권한있는 유저를 표시한다.
function grdMenuList_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    //메뉴명을 클릭했을 경우만 실행
    if (nCell == 3) {
        if ( !fcSearchCheck(obj) ) return;

        var sServiceName = "rsy3010-service:searchUser";
        var sInDataSet   = "";
        var sOutDataSet  = "dsUser=dsUser ";

        fcd_AddParam("MN_ID",   dsMenuAuth.GetColumn(nRow, "MN_ID"));
        fcd_AddParam("USER_NM", edSearchTxt.text);
        fcd_AddParam("TEAM_CD", cboTeamNm.Value);

        fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);
    }

}


//권한이양 버튼 클릭 시 권한이양 팝업을 호출한다.
function btnAuthTrans_OnClick(obj)
{
	nHandle = Open("common::comAuthTransPopup.xml", "sAuthGbn="+quote("002"), 294, 112, "Title=false");
}

function dsOutDept_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	dsOutDept.FireEvent = false; 
	var chkVal = dsOutDept.GetColumn(nRow, "CHK");
	var deptCd = dsOutDept.GetColumn(nRow, "DEPT_CD"); 
	var levl = dsOutDept.GetColumn(nRow, "LEVL"); 


	for (var j=nRow+1;j<dsOutDept.RowCount();j++)
	{  
		if ( dsOutDept.GetColumn(j, "LEVL") <= levl ){
			break;
		}

		dsOutDept.SetColumn(j, "CHK",  chkVal);

	}

	dsOutDept.FireEvent = true; 
}



function fcTvOnDblClick(obj,nRow)
{
	fcSearchAuthMenuList();
}

function cboMnGrpCd_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	for ( var i = 0; i < dsMenuAuth.rowcount; i++ ) {
        if(cboMnGrpCd.Index == 1) {
				dsMenuAuth.setColumn(i, "CHK", chkAuth.Value);
        } else {
            if(strCode == dsMenuAuth.getColumn(i, "UPUP_MN_ID")) {
				dsMenuAuth.setColumn(i, "CHK", chkAuth.Value);         
            } 
        }
	}
}

function fcEdOnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	switch ( obj.id ) {
		case "edSearchTxt" :
			if ( nChar == ENTER_KEY ) {		

				fcSearch();		

			}
			break;

	}
}
