	fcInit();

	return true;
}



var sParentDistNo;
var sParentEquipGbn;
/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj,sDistNo,sEquipGbn) {

	 sParentDistNo   = sDistNo;
	 sParentEquipGbn = sEquipGbn;

	 var sServiceName = "";
	 var sInDataSet   = "";
	 var sOutDataSet  = "";
	 if ( !fcSearchCheck(obj) ) return;

	 //장비이력조회 
	 sServiceName = "rsy4011-service:searchEquipHistCrd";
	 sInDataSet   = "";
	 sOutDataSet  = "dsEquipHistCrd=dsEquipHistCrd ";

	 fcd_AddParam("EQUIP_DIST_NO", sDistNo);
	 fcd_AddParam("CHECK_EQUIP_GBN", sEquipGbn);


	 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);


}


/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCntCheck() {
	// 변경된 자료가 있는지 체크


		//변경 카운트는 상위 폼에서 체크 
		if ( !fnc_DatasetChangeCheck(dsEquipHistCrd) && !fnc_DatasetChangeCheck(dsUploadFile)) {
			// 저장 할 자료가 없음

			return false;
		}

		return true;
}


/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	// 변경된 자료가 있는지 체크


		//변경 카운트는 상위 폼에서 체크 
		/*
		if ( !fnc_DatasetChangeCheck(dsEquipHistCrd) && !fnc_DatasetChangeCheck(dsUploadFile)) {
			// 저장 할 자료가 없음
			fnc_Message(SNIS_COM_1006, "N");
			return false;
		}*/

		// 필수항목체크
		oDataSet          = dsEquipHistCrd;
		sMandatoryColID   = "EQUIP_DIST_NO|CHECK_EQUIP_GBN";
		sMandatoryColName = "장비번호|장비구분";
		oGrid             =  grdEquipHistCrd ;

		if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
			return false;
		}


		return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {
	//if ( !fcSaveCheck() ) return;

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";

	var objParentForm = obj.getForm();



	sServiceName = "rsy4011-service:saveEquipHistCrd";
	sInDataSet   = "dsEquipHistCrd=dsEquipHistCrd:u dsUploadFile=dsUploadFile:u";
	//sInDataSet  += "dsSpecCd=dsSpecCd:u ";

	sOutDataSet  = "dsEquipHistCrd=dsEquipHistCrd ";



	//첨부파일 서버 업로드 

	if( dsUploadFile.GetRowCount() > 0 ) {
		for ( i = 0; i < dsUploadFile.GetRowCount(); i++ ) {
			if(dsUploadFile.getColumn(i, "CHK") == "1" ) {
				var rtn_arr = fnc_FileWrite(  objParentForm.divBtnLst.HttpFile 
											, dsUploadFile.GetColumn(i, "FILE_PATH" )
											, dsUploadFile.GetColumn(i, "FILE_NM" ) 
											,"");

				var fileSize = objParentForm.divBtnLst.HttpFile.GetFileSizeLocal(dsUploadFile.GetColumn(i, "FILE_PATH" ));

				if ( rtn_arr[0] != "SUCC" ) {
					alert(rtn_arr[1] + "파일 업로드 에러발생");
					return false;
				}

				//dsUploadFile.SetColumn(i, "SEQ_NO"      , dsOutNextSeqNo.getColumn(0,"NEXTSEQNO") );
				//dsUploadFile.SetColumn(i, "FILE_SEQ"    , toInteger(dsOutNextSeqNo.getColumn(0,"NEXTFILESEQNO")) + toInteger(i) );
				dsUploadFile.SetColumn(i, "FILE_PATH"    , rtn_arr[1] );
				dsUploadFile.SetColumn(i, "FILE_SIZE"   , fileSize );

			} // end if
		}
	} // end 


	fcd_AddParam("EQUIP_DIST_NO",dsEquipHistCrd.GetColumn(dsEquipHistCrd.currow,"EQUIP_DIST_NO"));
	fcd_AddParam("EQUIP_HIST_SEQ", dsEquipHistCrd.GetColumn(dsEquipHistCrd.currow,"EQUIP_HIST_SEQ"));
	fcd_AddParam("CHECK_EQUIP_GBN", dsEquipHistCrd.GetColumn(dsEquipHistCrd.currow,"CHECK_EQUIP_GBN"));


	//장비이력 저장 
	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);



}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {


	var parentObj =(this.getform()).getform();

	// DataSet의 Header 정보가 있을 때  Add 처리
	switch ( obj.GetForm().id ) {
		 case "dtlBtnRow":
			if ( fnc_DatasetChangeCheck(dsEquipHistCrd) ) {
				//fnc_Message(L_COM_ALT_0024, "N", "현재 작업중인 행이 존재하기 때문에\r\n 행을 추가할 수 ");
				//return;
			}

			nRow = dsEquipHistCrd.AddRow(); // Record Add

			dsEquipHistCrd.SetColumn(nRow,"EQUIP_DIST_NO", parentObj.sEquipDistNo );
			dsEquipHistCrd.SetColumn(nRow,"CHECK_EQUIP_GBN", parentObj.sEquipHistGbn );

			dsEquipHistCrd.SetColumn(nRow,"CHECK_STAT_CD", "001" );
			dsEquipHistCrd.SetColumn(nRow,"CHECK_TY_CD","001" );

			dsEquipHistCrd.SetColumn(nRow,"CHECK_DT",fcd_GetSysdate("YYYYMMDD"));

			calCheckDt.SetFocus();

			break;


		case "fileBtnRow":
			sResponse = fcd_GetSelectFile();	
			row = dsUploadFile.addRow();

			dsUploadFile.SetColumn(row, "ATT_FILE_KEY" , dsEquipHistCrd.GetColumn(dsEquipHistCrd.currow,"ATT_FILE_KEY"));


			if ( !fnc_IsNull(sResponse) ) {
				dsUploadFile.SetColumn(row, "CHK"       , "1"         );
				dsUploadFile.SetColumn(row, "FILE_PATH"  , sResponse[0] + "\\" + sResponse[1]);
				dsUploadFile.SetColumn(row, "FILE_NM" , sResponse[1]);


			}	

			if ( dsUploadFile.getColumn(row, "CHK") != "1") {
				dsUploadFile.rowcount = dsUploadFile.DeleteRow(row);
			}
			break;

	}
}


