/*-------------------------------------------------------------------------------------------------+
>>  첨부파일 조회 
+-------------------------------------------------------------------------------------------------*/
function fcSearchFile(sAttFileKey){

		var sServiceName = "";
		var sInDataSet   = "";
		var sOutDataSet  = "";


		 sServiceName = "rsy4011-service:searchFileMana";
		 sInDataSet   = "";
		 sOutDataSet  = "dsUploadFile=dsUploadFile ";

		 //조회조건 : 
		 fcd_AddParam("ATT_FILE_KEY", sAttFileKey);

		 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

}





function fcDsOnRowPosChanged(obj,nOldRow,nRow)
{		
	switch ( obj.id ) {

		//장비이력 목록 
		case "dsEquipHistCrd" :
			if(dsEquipHistCrd.GetRowType(dsEquipHistCrd.row) ==  "insert"){

					dsUploadFile.ClearData();

			}else if(dsEquipHistCrd.GetRowType(dsEquipHistCrd.row) ==  "normal" || dsEquipHistCrd.GetRowType(dsEquipHistCrd.row) ==  "update"){

					var sTmpKey = dsEquipHistCrd.GetColumn(dsEquipHistCrd.row,"ATT_FILE_KEY");

					if(sTmpKey == null || sTmpKey ==""){
						dsUploadFile.ClearData();
					}else{

						fcSearchFile(dsEquipHistCrd.GetColumn(dsEquipHistCrd.row,"ATT_FILE_KEY"));
					}


			}

			calCheckDt.SetFocus();

			break;


	}
}

