/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 Load時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormLoadSetting(obj) {  

    sBUTTONLIST   = 'TTTFTFT';

 	//화면환경 설정
	sFORMCAPTION  = obj.Title;
	sFORMLOCATION = "";

	//화면 정보 Display 및 Button 권한 설정
	fnc_FormLoadSetting(obj, sFORMCAPTION, sFORMLOCATION, sBUTTONLIST, sUSERAUTH);

    //공통코드 조회
	fcd_GetComboDs(dsInComCd);


	//초괴화면 : 기본정보
    tbLine.TabIndex = 0;

    cboSearchLineComp.SetFocus();

    fcsearch();

}

/*-------------------------------------------------------------------------------------------------+
>>  최초 화면 UNLoad時 처리 할 사항
+-------------------------------------------------------------------------------------------------*/
function fcFormUnloadProcess(obj) {
	// 자료 변경 여부를 체크하고자 하는 DataSet Object를 넘긴다.
	var sDataset = "dsLineMana";
	if (   !fnc_FormUnloadCheck(sDataset  , sFormId) ) return false;
}

/*-------------------------------------------------------------------------------------------------+
>>  단축키 처리(Form KeyDown)
+-------------------------------------------------------------------------------------------------*/
function fcHotKey(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

	// Return Key Press時 Next Component focus
	if ( nChar == 13 ) {
		var oFocusComponent = GetNextComponent(true);
		oFocusComponent.setfocus();
		return;
	}


}

/***************************************************************************************************
*                                      공통 버튼 Event 처리 부분                                   *
***************************************************************************************************/
/*-------------------------------------------------------------------------------------------------+
>>  취소 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInit(obj) {

	if(obj.ID == "btnSearch"){  //조회전 초기화 
		dsLineMana.ClearData();

		tbLine.tbEquipHist.divEquipHist.fcInit();
	}else if(obj.ID == "dsLineMana"){ // 
		tbLine.tbEquipHist.divEquipHist.fcInit();

	}else if(obj.ID == "btnCracel"){ // 초기화버튼 
		dsLineMana.ClearData();

		tbLine.tbEquipHist.divEquipHist.fcInit();

		tbLine.TabIndex = 0;
	}else{
		dsLineMana.ClearData();

		tbLine.tbEquipHist.divEquipHist.fcInit();

	}


}

/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 필수조건 체크 및 그리드 초기화
+-------------------------------------------------------------------------------------------------*/
function fcSearchCheck(obj) {

	fcInit(obj);

	return true;
}



/*-------------------------------------------------------------------------------------------------+
>>  조회 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSearch(obj,sDistNo,sEquipGbn) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";
	if ( !fcSearchCheck(obj) ) return;


	switch ( obj.Id ) {
		case "btnSearch":
			 sServiceName = "rsy4030-service:searchLineMana";
			 sInDataSet   = "";
			 sOutDataSet  = "dsLineMana=dsLineMana";

			 //조회조건 : 
			 fcd_AddParam("LINE_COMP", cboSearchLineComp.Value);
			 fcd_AddParam("PROD_NM", edSearchProdNm.Text);

			 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);



			break;

		case "dsLineMana" :			 
			  tbLine.tbEquipHist.divEquipHist.fcSearch(obj,sDistNo,sEquipGbn);

			 break;

		default:		// COMBO CHANGE 등 이벤트시 

			 sServiceName = "rsy4030-service:searchLineMana";
			 sInDataSet   = "";
			 sOutDataSet  = "dsLineMana=dsLineMana";

			 //조회조건 : 
			 fcd_AddParam("LINE_COMP", cboSearchLineComp.Value);
			 fcd_AddParam("PROD_NM", edSearchProdNm.Text);

			 fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", true);

			break;
	}





}




/*-------------------------------------------------------------------------------------------------+
>>  저장 하기전에 필수항목 체크
+-------------------------------------------------------------------------------------------------*/
function fcSaveCheck(obj) {
	// 변경된 자료가 있는지 체크

	if ( !fnc_DatasetChangeCheck(dsLineMana) && !tbLine.tbEquipHist.divEquipHist.fcSaveCntCheck() ) {
		// 저장 할 자료가 없음
		fnc_Message(SNIS_COM_1006, "N");
		return false;
	}


	// 필수항목체크
	oDataSet          = dsLineMana;
	sMandatoryColID   = "LINE_NO|SVR_NO|LINE_COMP|PROD_NM";
	sMandatoryColName = "회선번호|서비스번호|회선제공업체코드|상품명";
	oGrid             =  grdLineMana;
	if ( fnc_GetMandatoryViolateRow(oDataSet, sMandatoryColID, sMandatoryColName, oGrid) > -1 ) {
		return false;
	}


	return true;
}

/*-------------------------------------------------------------------------------------------------+
>>  저장 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcSave(obj) {

	var sServiceName = "";
	var sInDataSet   = "";
	var sOutDataSet  = "";


	if ( !fcSaveCheck() ) return;
	if ( !tbLine.tbEquipHist.divEquipHist.fcSaveCheck(obj) ) return;

	sServiceName = "rsy4030-service:saveLineMana";
	sInDataSet   = "dsLineMana=dsLineMana:u ";

	sOutDataSet  = "dsLineMana=dsLineMana ";


	fcd_SendTransaction(sServiceName, sInDataSet, sOutDataSet, "fcCallBack", false);

	tbLine.tbEquipHist.divEquipHist.fcSave(obj);

}

/*-------------------------------------------------------------------------------------------------+
>>  행추가 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcAddRow(obj) {
	// DataSet의 Header 정보가 있을 때  Add 처리
	switch ( obj.GetForm().id ) {
		case "grpBtnRow" :
			if ( fnc_DatasetChangeCheck(dsLineMana) ) {
				//fnc_Message(L_COM_ALT_0024, "N", "현재 작업중인 행이 존재하기 때문에\r\n 행을 추가할 수 ");
				//return;
			}

			nRow = dsLineMana.AddRow(); // Record Add

			dsLineMana.SetColumn(nRow,"LINE_COMP","001");
			dsLineMana.SetColumn(nRow,"INSTALL_LOC","001");
			dsLineMana.SetColumn(nRow,"JOIN_DT", fcd_GetSysdate("YYYYMMDD"));

			tbLine.TabIndex = 0;

			tbLine.tbLineMana.edLineNo.SetFocus();
			break;


	}
}


/*-------------------------------------------------------------------------------------------------+
>>  행삽입 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcInsRow(obj) {

	 switch ( obj.GetForm().id ) {
		case "grpBtnRow" :
			grdLineMana.InsertRow(grdLineMana.GetCurrow());           // Record Ins

			break;


	}

}


/*-------------------------------------------------------------------------------------------------+
>>  행삭제 버튼 클릭 時 처리
+-------------------------------------------------------------------------------------------------*/
function fcDelRow(obj) {
	// DataSet의 Header 정보가 있을 때  Add 처리
	switch ( obj.GetForm().id ) {
		case "grpBtnRow" :
			if ( !fnc_DeleteCheck(dsLineMana, "chk", "1") ) return;

			break;
	}
}


